VAR_GLOBAL
  /// Program fault : Invalid step
  ERR_INVALID_STEP : WORD := 1;
END_VAR

{attribute 'qualified_only'}
// ------------------------------------------------------------------------- 
//  SRCI Robot Library                                                
// ------------------------------------------------------------------------- 
//                                                                           
//  Object:      RobotLibraryConstants                                       
//  Author:      Thorsten Brach                                                
//  Date:        2024-06-01
//
//  Description:                                              
//   
//                                                                           
//  Copyright: 
//    (C) 2024 Thorsten Brach. All rights reserved                                                                         
//                                                                           
//  Disclaimer:                                                                           
//    This project is provided without any guarantee and can be used for   
//    private and commercial purposes. Any commercial use is at the user’s 
//    own risk and responsibility.
//
// -------------------------------------------------------------------------
VAR_GLOBAL CONSTANT
  /// Minimal amount of tools
  TOOL_MIN             : DINT :=  16;
  /// Maximal amount of tools
  TOOL_MAX             : DINT := 255;
  /// Minimal amount of frames
  FRAME_MIN            : DINT :=  16;
  /// Maximal amount of frames
  FRAME_MAX            : DINT := 255;
  /// Minimal amount of loads
  LOAD_MIN             : DINT :=  16;  
  /// Maximal amount of loads
  LOAD_MAX             : DINT := 255;  
  /// Minimal amount of work areas
  WORK_AREAS_MIN       : DINT :=   8;  
  /// Maximal amount of work areas
  WORK_AREAS_MAX       : DINT := 255;  
  /// Maximal amount of bytes which are allowed to be exchanged with the sub program of the RC
  SUB_PROGRAM_DATA_MAX : DINT := 190;

  /// Version of SRCI specification
  /// Bit 0-4 : Minor version = Features        (0..31)
  /// Bit 5-7 : Major version = Breaking change (0..07)
  SRCIVersion            : VersionStruct := ( MajorVersion := 0, MinorVersion := 1, PatchVersion := 0);

  /// OK = 0
  OK                   : DINT := 0;
  /// Running = 1
  RUNNING              : DINT := 1;
END_VAR

{attribute 'qualified_only'}
// ------------------------------------------------------------------------- 
//  SRCI Robot Library                                                
// ------------------------------------------------------------------------- 
//                                                                           
//  Object:      Parameter                                       
//  Author:      Thorsten Brach                                                
//  Date:        2024-06-01
//
//  Description:                                              
//   
//                                                                           
//  Copyright: 
//    (C) 2024 Thorsten Brach. All rights reserved                                                                         
//                                                                           
//  Disclaimer:                                                                           
//    This project is provided without any guarantee and can be used for   
//    private and commercial purposes. Any commercial use is at the user’s 
//    own risk and responsibility.
//
// -------------------------------------------------------------------------
VAR_GLOBAL CONSTANT
  /// Maximal amount of input data
  ROBOT_IN_DATA_MAX                   : UINT := 255;
  /// Maximal amount of output data
  ROBOT_OUT_DATA_MAX                  : UINT := 255;
  /// Maximal amount of tools
  TOOL_MAX                            : DINT := 255;
  /// Maximal amount of frames
  FRAME_MAX                           : DINT := 255;
  /// Maximal amount of loads
  LOAD_MAX                            : DINT := 255;  
  /// Maximal amount of work areas
  WORK_AREAS_MAX                      : DINT := 255;  
  /// Maximal amount of system logs
  SYSTEM_LOG_MAX                      : DINT := 32;  
  /// Maximal amount of message logs
  MESSAGE_LOG_MAX                     : DINT := 32;  
  /// Maximal amount of List entries 
  LIST_ENTRIES_MAX                    : DINT := 100;
  /// Maximal amount of bytes for the telegram payload
  TELEGRAM_PAYLOAD_MAX                : DINT := 255;  
  /// Maximal amount of bytes for the parameter payload
  PARAMETER_PAYLOAD_MAX               : DINT := 128;  
  /// Maximal amount of bytes for the response payload
  RESPONSE_PAYLOAD_MAX                : DINT := 128;  
  /// Maximal amount of bytes that can be exchanged with the sub program on the RC
  SUB_PROGRAM_DATA_MAX                : DINT := 64;
  /// Maximal amount of spline data
  SPLINE_DATA_MAX                     : DINT := 64;
  /// Maximal amount of entries in the Active Command Register
  ACTIVE_CMD_REGISTER_ENTRIES_MAX     : DINT := 50;

  /// Flag to indicate that the byte order must be changed
  SWAP_BYTE_ORDER                     : BOOL := TRUE; {warning 'ToDo: Detect local byte order'}

END_VAR

