//    own risk and responsibility.
//
// -------------------------------------------------------------------------
TYPE Severity :
(
  /// Debug message
  /// • No user action required
  DEBUG := 4, 
  /// Informative message
  /// • No user action required
  INFO := 5,
  /// Warning message
  /// • User action will be required at some point
  WARNING := 20,
  /// Error message
  /// • User action required immediately
  ERROR := 28,
  ///  Fatal error message
  /// • User action required immediately
  /// • Reinitialization OF RI required
  FATAL_ERROR := 29
)USINT;
END_TYPE

{attribute 'qualified_only'}
// ------------------------------------------------------------------------- 
//  SRCI Robot Library                                                
// ------------------------------------------------------------------------- 
//                                                                           
