  TRIGGER_BUFFERED := 10,
  /// Command is buffered in sequence buffer, aborts and empties previous commands in sequence buffer, and is executed once (Trigger based)
  TRIGGER_ABORTING := 11,
  /// Command is buffered in parallel buffer and executed once (Trigger based)
  TRIGGER_ONCE := 12,
  /// Command is buffered in parallel buffer and executed repeatedly until deliberate deactivation by user (Trigger based)
  TRIGGER_CONTINUOUS := 13,
  /// Command is buffered and executed multiple times when triggered. The CMD remains in the buffer until removed by the user.
  TRIGGER_MULTIPLE := 14
)USINT;
END_TYPE

{attribute 'qualified_only'}
// ------------------------------------------------------------------------- 
//  SRCI Robot Library                                                
// ------------------------------------------------------------------------- 
//                                                                           
//  Object:      ResistanceForceMode                                       
//  Author:      Thorsten Brach                                                
//  Date:        2024-06-01
//
//  Description:                                              
//   
//                                                                           
//  Copyright: 
//    (C) 2024 Thorsten Brach. All rights reserved                                                                         
//                                                                           
//  Disclaimer:                                                                           
//    This project is provided without any guarantee and can be used for   
//    private and commercial purposes. Any commercial use is at the user’s 
//    own risk and responsibility.
//
// -------------------------------------------------------------------------
TYPE ResistanceForceMode :
(
  /// 0 (default): ResistanceForceTCP.
  /// Set "0" to set the resistance of the RA against the external force at the TCP.
  RESISTANCE_FORCE_TCP := 0,

  /// 1: ResistanceForceAxis.
  /// Set "1" to set the resistance of the RA against the external force for each axis independently.
  RESISTANCE_FORCE_AXIS := 1
)USINT;
END_TYPE

