  //OrderNumber       : ARRAY[0..1] OF UDINT;
  /// Payload defined by type per CMD definition. Processed by Appl. Layer Task.
  Payload           : ARRAY[0..RobotLibraryParameter.TELEGRAM_PAYLOAD_MAX] OF BYTE;
  /// Payload length
  PayloadLen        : UDINT;  
  /// Payload poiner   
  PayLoadPtr        : DWORD;
END_STRUCT
END_TYPE

