  CartesianForce        :  AxesGroupCyclicOptionalDataCartesianForce;
  /// current force for the external axis (E1, E2, E3, E4, E5, E6)
  CartesianForceExt     :  AxesGroupCyclicOptionalDataCartesianForceExt;
