//
// -------------------------------------------------------------------------
TYPE TelegramFooter :
STRUCT
  /// Bit 00: Connection alive signal
  LifeSign : BOOL;
  /// Bit 01 
  Bit01    : BOOL;
  /// Bit 02 
  Bit02    : BOOL;
  /// Bit 03 
  Bit03    : BOOL;
  /// Bit 04 
  Bit04    : BOOL;
  /// Bit 05 
  Bit05    : BOOL;
  /// Bit 06 
  Bit06    : BOOL;
  /// Bit 07 
  Bit07    : BOOL;    
END_STRUCT
END_TYPE

{attribute 'pack_mode' := '1'} 
// ------------------------------------------------------------------------- 
//  SRCI Robot Library                                                
// ------------------------------------------------------------------------- 
//                                                                           
//  Object:      TelegramRobToPlcCyclic                                       
//  Author:      Thorsten Brach                                                
//  Date:        2024-06-01
//
//  Description:                                              
//   
//                                                                           
//  Copyright: 
//    (C) 2024 Thorsten Brach. All rights reserved                                                                         
//                                                                           
//  Disclaimer:                                                                           
//    This project is provided without any guarantee and can be used for   
//    private and commercial purposes. Any commercial use is at the user’s 
//    own risk and responsibility.
//
// -------------------------------------------------------------------------
TYPE TelegramRobToPlcHeader :
STRUCT
  /// Version of SRCI specification
  /// Bit 0-4 : Minor version = Features        (0..31)
  /// Bit 5-7 : Major version = Breaking change (0..07)
  SRCIVersion   : VersionStruct;
  /// Connection alive signal
  LifeSign      : BYTE;
  /// Reserved byte
  Reserved      : BYTE;
  /// Initialization and Telegram control state
  TelegramState : USINT;
  /// Combination of various RA related states.
  StatusRobotArm: RaStatusWord;
  /// Actual override in percentage encoding 
  Override      : UINT;
END_STRUCT
END_TYPE

