ST.xhtml:
IF ( RobotLibraryParameter.SWAP_BYTE_ORDER AND Size = 2)
THEN
 _pLwLb := pValue + 0;
 _pLwHb := pValue + 1;

  // swap bytes
 _tmpBytes[1] := _pLwHb^;
 _tmpBytes[2] := _pLwLb^;

  // copy bytes to data type
  MemCpy(pValue, ADR(_tmpBytes), Size);
END_IF


IF ( RobotLibraryParameter.SWAP_BYTE_ORDER AND Size = 4)
THEN
 _pLwLb := pValue + 0;
 _pLwHb := pValue + 1;
 _pHwLb := pValue + 2;
 _pHwHb := pValue + 3;

  // swap bytes
 _tmpBytes[1] := _pHwHb^;
 _tmpBytes[2] := _pHwLb^;
 _tmpBytes[3] := _pLwHb^;
 _tmpBytes[4] := _pLwLb^;

  // copy bytes to data type
  MemCpy(pValue, ADR(_tmpBytes),Size);  
END_IF

