FUNCTION_BLOCK RobotLibraryBaseFB
// ------------------------------------------------------------------------- 
//  SRCI Robot Library                                                
// ------------------------------------------------------------------------- 
//                                                                           
//  Object:      RobotLibraryBaseFB                                       
//  Author:      Thorsten Brach                                                
//  Date:        2024-06-01
//
//  Description:                                              
//   
//                                                                           
//  Copyright: 
//    (C) 2024 Thorsten Brach. All rights reserved                                                                         
//                                                                           
//  Disclaimer:                                                                           
//    This project is provided without any guarantee and can be used for   
//    private and commercial purposes. Any commercial use is at the user’s 
//    own risk and responsibility.
//
// -------------------------------------------------------------------------
VAR_INPUT
  /// User defined command name
  Name         : STRING(20);
END_VAR
VAR_OUTPUT
  /// Command data
  CommandData  : SendData;
  /// response data
  ResponseData : RecvData;
  /// An error occurred during the execution of the command
  Error        : BOOL;
  /// ErrorID reported by RC for error identification according to Table 7-1
  ErrorID      : WORD;
  /// WarningID for warning identification reported during execution of command according to Table 7-3
  WarningID    : WORD;
  /// InfoID for info identification reported during execution of command according to Table 7-5
  InfoID       : WORD;    
END_VAR
VAR_IN_OUT
  /// Robot assignment of function
  AxesGroup    : AxesGroup; 
END_VAR

VAR
  /// unique ID
 _uniqueID         : UDINT;  
  /// flag for response received
 _responseReceived : BOOL;
  /// internal step counter
 _stepCmd          : DINT;
  /// internal timer 
 _timerCmd         : TON;
  /// internal timeout 
 _timeoutCmd       : TIME := T#5S;


  /// internal command header
 _cmdHeader        : CmdHeader;  
  /// internal response header
 _rspHeader        : RspHeader;

END_VAR

ST.xhtml:
OnCall            (AxesGroup    := AxesGroup);

CmdManagementLayer(AxesGroup    := AxesGroup);

NetworkLayer     ( AxesGroup    := AxesGroup,
                   RobotInData  := RobotInData,
                   RobotOutData := RobotOutData);

TransportLayer   ( AxesGroup    := AxesGroup);

FOR _idx := 1 TO RobotLibraryParameter.ACTIVE_CMD_REGISTER_ENTRIES_MAX
DO
  // Check reference to command FB is valid ? 
  IF ( NOT __ISVALIDREF(AxesGroup.Acyclic.ActiveCommandRegister.Register[_idx].rCommandFB))
  THEN
    RETURN;
	END_IF

  // Check Cmd Parameter changed ? 
  IF ( AxesGroup.Acyclic.ActiveCommandRegister.Register[_idx].rCommandFB.NotifyParameterChanged )
  THEN
    IF (( AxesGroup.Acyclic.ActiveCommandRegister.Register[_idx].Command[1].State <> BufferStateCmd.SENDING   ) AND
        ( AxesGroup.Acyclic.ActiveCommandRegister.Register[_idx].Command[1].State <> BufferStateCmd.PROCESSED ))
    THEN
      // add to send register
      AxesGroup.Acyclic.ActiveCommandRegister.Register[_idx].Command[1].State      := BufferStateCmd.UPDATE_AVAILABLE;
      AxesGroup.Acyclic.ActiveCommandRegister.Register[_idx].Command[1].Payload    := AxesGroup.Acyclic.ActiveCommandRegister.Register[_idx].rCommandFB.CommandData.Payload;
      AxesGroup.Acyclic.ActiveCommandRegister.Register[_idx].Command[1].PayloadLen := AxesGroup.Acyclic.ActiveCommandRegister.Register[_idx].rCommandFB.CommandData.PayloadLen;
    ELSE
      // add to buffer register
      AxesGroup.Acyclic.ActiveCommandRegister.Register[_idx].Command[2].State      := BufferStateCmd.UPDATE_AVAILABLE;
      AxesGroup.Acyclic.ActiveCommandRegister.Register[_idx].Command[2].Payload    := AxesGroup.Acyclic.ActiveCommandRegister.Register[_idx].rCommandFB.CommandData.Payload;
      AxesGroup.Acyclic.ActiveCommandRegister.Register[_idx].Command[2].PayloadLen := AxesGroup.Acyclic.ActiveCommandRegister.Register[_idx].rCommandFB.CommandData.PayloadLen;
		END_IF
  END_IF

  // Check Response received ? 
  IF ( AxesGroup.Acyclic.ActiveCommandRegister.Register[_idx].Response[1].State = BufferStateRsp.RECEIVED)
  THEN
    // update state
    AxesGroup.Acyclic.ActiveCommandRegister.Register[_idx].Response[1].State := BufferStateRsp.PROCESSED;
    // callback CommandFB    
    AxesGroup.Acyclic.ActiveCommandRegister.Register[_idx].rCommandFB.CallBack(AxesGroup.Acyclic.ActiveCommandRegister.Register[_idx].Response[1]);    
	END_IF

  // delete entries with State.IS_Final 
  IF ( AxesGroup.Acyclic.ActiveCommandRegister.Register[_idx].State  = ActiveCommandRegisterState.IS_FINAL )
  THEN
    MemSet(ADR(AxesGroup.Acyclic.ActiveCommandRegister.Register[_idx]), 0, SIZEOF(AxesGroup.Acyclic.ActiveCommandRegister.Register[_idx]));
	END_IF

END_FOR

InterfaceAsPlainText.xhtml:
METHOD CmdManagementLayer : BOOL
VAR_IN_OUT
  /// Robot assignment of function
  AxesGroup : AxesGroup;
END_VAR

VAR
  /// internal index
 _idx     : DINT;  
END_VAR

