AxesGroup.Config.PlcToRob.Header.TelegramNumberPlcToRob.12 := AxesGroup.Parameter.OptionalCyclic.RobToPlc.UseCurrentEct;
AxesGroup.Config.PlcToRob.Header.TelegramNumberPlcToRob.13 := AxesGroup.Parameter.OptionalCyclic.RobToPlc.Bit13;  
AxesGroup.Config.PlcToRob.Header.TelegramNumberPlcToRob.14 := AxesGroup.Parameter.OptionalCyclic.RobToPlc.Bit14;
AxesGroup.Config.PlcToRob.Header.TelegramNumberPlcToRob.15 := AxesGroup.Parameter.OptionalCyclic.RobToPlc.Bit15;  


AxesGroup.Config.PlcToRob.Header.ClientDate := DATE_TO_IEC_DATE(DATE#2024-06-01 ); {warning 'ToDo: Get current date from target system'}
AxesGroup.Config.PlcToRob.Header.ClientTime := TIME_TO_IEC_TIME(TOD#12:00:00.000); {warning 'ToDo: Get current time from target system'}

// reset all variables
SendData.Reset();

// PlcToRob.Cyclic {{{

// PlcToRob.Cyclic.SRCIVersion  
SendData.AddByte     (VersionToByte(AxesGroup.Config.PlcToRob.Header.SRCIVersion));
// PlcToRob.Cyclic.FastStop_LifeSign
SendData.AddHalfBytes(AxesGroup.Config.PlcToRob.Header.FastStop, AxesGroup.Config.PlcToRob.Header.LifeSign); 
// PlcToRob.Cyclic.TelegramLengthPlcToRob
SendData.AddUint     (AxesGroup.Config.PlcToRob.Header.TelegramLengthPlcToRob);
// PlcToRob.Cyclic.TelegramLengthRobToPlc 
SendData.AddUint     (AxesGroup.Config.PlcToRob.Header.TelegramLengthRobToPlc);
// PlcToRob.Cyclic.AxesGroupID_Control
SendData.AddHalfBytes(AxesGroup.Config.PlcToRob.Header.AxesGroupID, AxesGroup.Config.PlcToRob.Header.Control);
// PlcToRob.Cyclic.Reserved
SendData.AddByte     (AxesGroup.Config.PlcToRob.Header.Reserved);
// PlcToRob.Cyclic.TelegramNumberPlcToRob 
SendData.AddUint     (AxesGroup.Config.PlcToRob.Header.TelegramNumberPlcToRob);
// PlcToRob.Cyclic.TelegramNumberRobToPlc 
SendData.AddUint     (AxesGroup.Config.PlcToRob.Header.TelegramNumberRobToPlc);
// PlcToRob.Cyclic.ClientDate
SendData.AddUint     (AxesGroup.Config.PlcToRob.Header.ClientDate); 
// PlcToRob.Cyclic.ClientTime
SendData.AddTime     (AxesGroup.Config.PlcToRob.Header.ClientTime);  
// }}}

// PlcToRob.CyclicOptional Data {{{

// AxesGroup.OptionalCyclic.PlcToRob.SubProgramData {{{
IF ( AxesGroup.OptionalCyclic.PlcToRob.SubProgramData.Active )
THEN
  SendData.AddDataBlock( ADR(AxesGroup.OptionalCyclic.PlcToRob.SubProgramData.Data), SIZEOF(AxesGroup.OptionalCyclic.PlcToRob.SubProgramData.Data));  
END_IF
// }}}

// AxesGroup.OptionalCyclic.PlcToRob.CartesianPos {{{
IF ( AxesGroup.OptionalCyclic.PlcToRob.CartesianPosition.Active) 
THEN
  // AxesGroup.OptionalCyclic.PlcToRob.CartesianPos.X
  SendData.AddReal(AxesGroup.OptionalCyclic.PlcToRob.CartesianPosition.X);
  // AxesGroup.OptionalCyclic.PlcToRob.CartesianPos.Y
  SendData.AddReal( AxesGroup.OptionalCyclic.PlcToRob.CartesianPosition.Y);
  // AxesGroup.OptionalCyclic.PlcToRob.CartesianPos.Z
  SendData.AddReal( AxesGroup.OptionalCyclic.PlcToRob.CartesianPosition.Z);
  // AxesGroup.OptionalCyclic.PlcToRob.CartesianPos.Rx
  SendData.AddReal( AxesGroup.OptionalCyclic.PlcToRob.CartesianPosition.Rx);
  // AxesGroup.OptionalCyclic.PlcToRob.CartesianPos.Ry
  SendData.AddReal( AxesGroup.OptionalCyclic.PlcToRob.CartesianPosition.Ry);
  // AxesGroup.OptionalCyclic.PlcToRob.CartesianPos.Rz
  SendData.AddReal( AxesGroup.OptionalCyclic.PlcToRob.CartesianPosition.Rz);
  // AxesGroup.OptionalCyclic.PlcToRob.CartesianPos.Config
 _tmpByte   := 0;
 _tmpByte.0 := AxesGroup.OptionalCyclic.PlcToRob.CartesianPosition.Config.Shoulder = ArmConfigShoulder.BACK;
 _tmpByte.1 := AxesGroup.OptionalCyclic.PlcToRob.CartesianPosition.Config.Elbow    = ArmConfigElbow   .DOWN;
 _tmpByte.2 := AxesGroup.OptionalCyclic.PlcToRob.CartesianPosition.Config.Wrist    = ArmConfigWrist   .FLIP;

  SendData.AddByte(_tmpByte);

  // AxesGroup.OptionalCyclic.PlcToRob.CartesianPos.TurnNumber.J1 + J2
  SendData.AddHalfBytes( SINT_TO_BYTE(AxesGroup.OptionalCyclic.PlcToRob.CartesianPosition.TurnNumber.J1Turns), SINT_TO_BYTE(AxesGroup.OptionalCyclic.PlcToRob.CartesianPosition.TurnNumber.J2Turns));
  // AxesGroup.OptionalCyclic.PlcToRob.CartesianPos.TurnNumber.J3 + J4
  SendData.AddHalfBytes( SINT_TO_BYTE(AxesGroup.OptionalCyclic.PlcToRob.CartesianPosition.TurnNumber.J3Turns), SINT_TO_BYTE(AxesGroup.OptionalCyclic.PlcToRob.CartesianPosition.TurnNumber.J4Turns));
  // AxesGroup.OptionalCyclic.PlcToRob.CartesianPos.TurnNumber.J5 + J6
  SendData.AddHalfBytes( SINT_TO_BYTE(AxesGroup.OptionalCyclic.PlcToRob.CartesianPosition.TurnNumber.J5Turns), SINT_TO_BYTE(AxesGroup.OptionalCyclic.PlcToRob.CartesianPosition.TurnNumber.J6Turns));
  // AxesGroup.OptionalCyclic.PlcToRob.CartesianPos.TurnNumber.E1 
  SendData.AddSint(AxesGroup.OptionalCyclic.PlcToRob.CartesianPosition.TurnNumber.E1Turns);
  // AxesGroup.OptionalCyclic.PlcToRob.CartesianPos.E1
  SendData.AddReal( AxesGroup.OptionalCyclic.PlcToRob.CartesianPosition.E1);
  // AxesGroup.OptionalCyclic.PlcToRob.CartesianPos.CoordinateSystem.ToolNo
  SendData.AddUsint(AxesGroup.OptionalCyclic.PlcToRob.CartesianPosition.CoordinateSystem.ToolNo);
  // AxesGroup.OptionalCyclic.PlcToRob.CartesianPos.CoordinateSystem.FrameNo 
  SendData.AddUsint(AxesGroup.OptionalCyclic.PlcToRob.CartesianPosition.CoordinateSystem.FrameNo);  
END_IF
// }}}

// AxesGroup.OptionalCyclic.PlcToRob.CartesianPosExt {{{
IF ( AxesGroup.OptionalCyclic.PlcToRob.CartesianPositionExt.Active) 
THEN
  // AxesGroup.OptionalCyclic.PlcToRob.CartesianPosExt.E2 
  SendData.AddReal( AxesGroup.OptionalCyclic.PlcToRob.CartesianPositionExt.E2);
  // AxesGroup.OptionalCyclic.PlcToRob.CartesianPosExt.E3
  SendData.AddReal( AxesGroup.OptionalCyclic.PlcToRob.CartesianPositionExt.E3);
  // AxesGroup.OptionalCyclic.PlcToRob.CartesianPosExt.E4
  SendData.AddReal( AxesGroup.OptionalCyclic.PlcToRob.CartesianPositionExt.E4);
  // AxesGroup.OptionalCyclic.PlcToRob.CartesianPosExt.E5
  SendData.AddReal( AxesGroup.OptionalCyclic.PlcToRob.CartesianPositionExt.E5);
  // AxesGroup.OptionalCyclic.PlcToRob.CartesianPosExt.E6
  SendData.AddReal( AxesGroup.OptionalCyclic.PlcToRob.CartesianPositionExt.E6);
END_IF    
// }}}

// AxesGroup.OptionalCyclic.PlcToRob.JointPosition {{{
IF ( AxesGroup.OptionalCyclic.PlcToRob.JointPosition.Active) 
THEN
  // AxesGroup.OptionalCyclic.PlcToRob.JointPosition.J1
  SendData.AddReal( AxesGroup.OptionalCyclic.PlcToRob.JointPosition.J1);
  // AxesGroup.OptionalCyclic.PlcToRob.JointPosition.J2
