{warning 'ToDo: Sequence Handling for Seq 0..1'}
// PlcToRob.Sequence[0]{{{

// PlcToRob.Sequence[0].Header{{{

// AxesGroup.OptionalCyclic.PlcToRob.Sequence[0].Header.SEQ_ACK 
SendData.AddUint( Telegram.PlcToRob.Sequence[0].Header.SEQ_ACK);
// AxesGroup.OptionalCyclic.PlcToRob.Sequence[0].Header.PayloadLength
SendData.AddUint( Telegram.PlcToRob.Sequence[0].Header.PayloadLength);
// }}}


{warning 'ToDo: Fragment Handling for Frag 0..9'}
// PlcToRob.Sequence[0].Fragment {{{



// PlcToRob.Sequence[0].Fragment.Header {{{

// AxesGroup.OptionalCyclic.PlcToRob.Sequence[0].Fragment.Header.ID 
SendData.AddUint( Telegram.PlcToRob.Sequence[0].Fragment[0].Header.ID);
// AxesGroup.OptionalCyclic.PlcToRob.Sequence[0].Fragment.Header.Reserve
SendData.AddByte(Telegram.PlcToRob.Sequence[0].Fragment[0].Header.Reserve);
// AxesGroup.OptionalCyclic.PlcToRob.Sequence[0].Fragment.Header.FragmentAction 
SendData.AddByte(FragmentActionToByte(Telegram.PlcToRob.Sequence[0].Fragment[0].Header.FragmentAction));
// AxesGroup.OptionalCyclic.PlcToRob.Sequence[0].Fragment.Header.PayloadPointer
SendData.AddUint(Telegram.PlcToRob.Sequence[0].Fragment[0].Header.PayloadPointer);
// AxesGroup.OptionalCyclic.PlcToRob.Sequence[0].Fragment.Header.PayloadLength 
SendData.AddUint(Telegram.PlcToRob.Sequence[0].Fragment[0].Header.PayloadLength);

// PlcToRob.Sequence[0].Fragment.Command {{{    
FOR _payLoadPtr := AxesGroup.Acyclic.ActiveCommandRegister.Register[1].Command[1].PayLoadPtr TO AxesGroup.Acyclic.ActiveCommandRegister.Register[1].Command[1].PayloadLen   
DO
  // copy payload
  SendData.AddByte(AxesGroup.Acyclic.ActiveCommandRegister.Register[1].Command[1].Payload[_payLoadPtr]);

  // inc payload pointer
  AxesGroup.Acyclic.ActiveCommandRegister.Register[1].Command[1].PayLoadPtr 
    := AxesGroup.Acyclic.ActiveCommandRegister.Register[1].Command[1].PayLoadPtr + 1;

  // set command state
  IF ( AxesGroup.Acyclic.ActiveCommandRegister.Register[1].Command[1].PayLoadPtr >= AxesGroup.Acyclic.ActiveCommandRegister.Register[1].Command[1].PayLoadLen) 
  THEN
    AxesGroup.Acyclic.ActiveCommandRegister.Register[1].Command[1].State := BufferStateCmd.PROCESSED;
  ELSE
    AxesGroup.Acyclic.ActiveCommandRegister.Register[1].Command[1].State := BufferStateCmd.SENDING;  
  END_IF 

  // check limit reached ?
  IF ( SendData.PayloadLen >= RobotLibraryParameter.ROBOT_OUT_DATA_MAX - SIZEOF(Telegram.PlcToRob.Footer)) 
  THEN
    EXIT;
  END_IF  
END_FOR    
// }}}
// }}}
// }}}
// }}}


// Footer {{{

// PlcToRob.Footer.Reserved_LifeSign
SendData.AddHalfBytes(0, AxesGroup.Config.PlcToRob.Header.LifeSign);

// }}}

