VAR_IN_OUT
  /// Robot assignment of function
  AxesGroup         : AxesGroup;
    /// Inputs of PLC for communication from RC
  RobotInData       : ARRAY[1..RobotLibraryParameter.ROBOT_IN_DATA_MAX ] OF BYTE;
  /// Outputs of PLC for communication to RC
  RobotOutData      : ARRAY[1..RobotLibraryParameter.ROBOT_OUT_DATA_MAX] OF BYTE;
END_VAR

