METHOD ParseRecvData
VAR_IN_OUT
  /// Robot assignment of function
  AxesGroup    : AxesGroup;
    /// Inputs of PLC for communication from RC
  RobotInData  : ARRAY[1..RobotLibraryParameter.ROBOT_IN_DATA_MAX ] OF BYTE;
  /// Outputs of PLC for communication to RC
  RobotOutData : ARRAY[1..RobotLibraryParameter.ROBOT_OUT_DATA_MAX] OF BYTE;
END_VAR

VAR
  /// internal index
 _idx      : DINT;
  /// temporary DWORD
 _tmpDword : DWORD;
END_VAR

METHOD TransportLayer
VAR_IN_OUT
   /// Robot assignment of function
  AxesGroup : AxesGroup;
END_VAR

/// Handles multiple mechanisms required for operation of the interface.
FUNCTION_BLOCK RobotTask
// ------------------------------------------------------------------------- 
//  SRCI Robot Library                                                
// ------------------------------------------------------------------------- 
//                                                                           
//  Object:      RobotTask                                       
//  Author:      Thorsten Brach                                                
//  Date:        2024-06-01
//
//  Description:                                              
//   
//                                                                           
//  Copyright: 
//    (C) 2024 Thorsten Brach. All rights reserved                                                                         
//                                                                           
//  Disclaimer:                                                                           
//    This project is provided without any guarantee and can be used for   
//    private and commercial purposes. Any commercial use is at the user’s 
//    own risk and responsibility.
//
// -------------------------------------------------------------------------
VAR_INPUT
  /// Set TRUE (default) to initialize the interface
  Enable            : BOOL;
  /// User defined robot name
  RobotName         : STRING(20);
END_VAR

VAR_IN_OUT
  /// Inputs of PLC for communication from RC
  RobotInData       : ARRAY[1..RobotLibraryParameter.ROBOT_IN_DATA_MAX ] OF BYTE;
  /// Outputs of PLC for communication to RC
  RobotOutData      : ARRAY[1..RobotLibraryParameter.ROBOT_OUT_DATA_MAX] OF BYTE;
  ///  User data stored on the PLC according to Table 6-10
  UserData          : UserData;
  /// ToolData stored on PLC. For more information refer to 5.5.6.3
  ToolData          : ARRAY[1..RobotLibraryParameter.TOOL_MAX      ] OF ToolData;
  /// LoadData stored on PLC.For more information refer to 5.5.6.4
  FrameData         : ARRAY[1..RobotLibraryParameter.FRAME_MAX     ] OF FrameData;
  /// LoadData stored on PLC.For more information refer to 5.5.6.4
  LoadData          : ARRAY[1..RobotLibraryParameter.LOAD_MAX      ] OF LoadData;
  /// Work areas stored on PLC. For more information refer to 5.5.8
  WorkAreas         : ARRAY[1..RobotLibraryParameter.WORK_AREAS_MAX] OF RobotWorkArea;
  /// Software limits stored on PLC
  SoftwareLimits    : SWLimits;
  /// Default dynamics stored on PLC. For more information refer to 5.5.7
  DefaultDynamics   : DefaultDynamics;
  /// Reference dynamics stored on PLC. For more information refer to 5.5.7
  ReferenceDynamics : ReferenceDynamics;
  /// System log on PLC
  SystemLog         : ARRAY[1..RobotLibraryParameter.SYSTEM_LOG_MAX ] OF STRING;
  ///Message buffer on the PLC - For more information refer to 5.5.11
  MessageLog        : ARRAY[1..RobotLibraryParameter.MESSAGE_LOG_MAX] OF AlarmMessage;
  /// Robot assignment of function
  AxesGroup         : AxesGroup;
END_VAR

VAR_OUTPUT
  /// FB is being processed
  Busy              : BOOL;
  /// Interface is initialized (RI state: "Initialized").
  /// For more information on RI states refer to chapter 5.5.3.1.
  Initialized       : BOOL;
  /// Server and client were successfully synchronized (RI state: "Synchronized).
  /// For more information on the synchronization mechanism refer to chapter 5.6.7.
  Synchronized      : BOOL;
  /// An error occurred
  Error             : BOOL;
  /// ErrorID reported by RC for error identification according to Table 7-1.
  ErrorID           : WORD;
  /// WarningID for warning identification reported during execution of command according to Table 7-3.
  WarningID         : WORD;
  /// InfoID for info identification reported during execution of command according to Table 7-5.
  InfoID            : WORD;
END_VAR

VAR
  /// Execution order lost
  ExecutionOrderList : ARRAY [1..RobotLibraryParameter.ACTIVE_CMD_REGISTER_ENTRIES_MAX] OF DINT;  
  /// Send Buffer
  SendData           : SendData;
  /// Recv Buffer
  RecvData           : RecvData;
  /// Telegram 
  Telegram           : Telegram;
END_VAR

ST.xhtml:
// compare memory 
CheckParameterChanged := ( MemCmp(ADR(ParCmd), ADR(_parCmd), SIZEOF(ParCmd)) = RobotLibraryConstants.OK );

IF ( CheckParameterChanged )
THEN  
  // update internal copy of parameters 
 _parCmd := ParCmd;
  // inc parameter sequence
 _command.ParSeq := _command.ParSeq + 1;
  // update command data  
  CommandData := CreateCommandData(); // ( Access via reference to rCommandFB in ACR )
END_IF

// Table 6-662: Sent CMD payload (PLC to RC) of "ActivateNextCommand"

// --------------------------
// Header
// --------------------------
// Byte 00 : UINT  - Type HB     
// Byte 01 :       - Type LB    
// Byte 02 : USINT - Reserve | ExecutionMode
// Byte 03 : USINT - ParSeq  | Priority
// --------------------------
// Datablock
// --------------------------
// Byte 04 : SINT - EmitterID[0]
// Byte 05 : SINT - EmitterID[1]
// Byte 06 : SINT - EmitterID[2]   
// Byte 07 : SINT - EmitterID[3]
// Byte 08 : SINT - ListenerID
// Byte 09 : BYTE - Reserve

// --------------------------

// set command parameter 
_command.CmdTyp     :=  CmdType.ActivateNextCommand;
_command.ExecMode   :=  ExecutionMode.PARALLEL;
_command.ParSeq     := _command.ParSeq;
_command.Priority   :=  PriorityLevel.NORMAL;
_command.ListenerID := _parCmd.ListenerID;


// copy command data to header
_cmdHeader := _command;
// call base implementation to copy header to payload buffer
SUPER^.CreateCommandData();

// add command.EmitterID[0]
CreateCommandData.AddSint(_command.EmitterID[0]);
// add command.EmitterID[1]
CreateCommandData.AddSint(_command.EmitterID[1]);
// add command.EmitterID[2]
CreateCommandData.AddSint(_command.EmitterID[2]);
// add command.EmitterID[3]
CreateCommandData.AddSint(_command.EmitterID[3]);
// add command.ListenerID
CreateCommandData.AddSint(_command.ListenerID);
// add command.Reserve
CreateCommandData.AddByte(_command.Reserve);

CASE _stepCmd OF

  00:  IF ( Execute ) 
       THEN
         // set busy flag
         Busy := TRUE;
         // Reset command outputs
         MemSet(ADR(OutCmd),0,SIZEOF(OutCmd));
         // apply command parameter
        _parCmd := ParCmd;
         // init parameter sequence
        _command.ParSeq := 1;
         // set timeout
         SetTimeout(PT := _timeoutCmd, rTimer := _timerCmd);
         // inc step counter
        _stepCmd := _stepCmd + 1; 
	     END_IF

  01:  // create command data
       CommandData := CreateCommandData();
       // Add command to active command register
      _uniqueID := AxesGroup.Acyclic.ActiveCommandRegister.AddCmd( rCommandFB := THIS^ );     
       // set timeout
       SetTimeout(PT := _timeoutCmd, rTimer := _timerCmd);
       // inc step counter
      _stepCmd := _stepCmd + 1; 

  02:  // Wait for responce received
       IF ( _responseReceived )
       THEN
         // reset response received flag
        _responseReceived := FALSE;

         CASE  _response.State 
         OF
           CmdMessageState.BUFFERED    : CommandBuffered    := TRUE;
         //CmdMessageState.ACTIVE      : Active             := TRUE;
         //CmdMessageState.INTERRUPTED : CommandInterrupted := TRUE;
         //CmdMessageState.ABORTED     : CommandAborted     := TRUE;
           CmdMessageState.ERROR       : Error              := TRUE;
				 END_CASE

         // Done, Aborted or Error ?
         IF (_response.State >= CmdMessageState.DONE )
         THEN
           // Update results
           OutCmd.InvocationCounter := _response.InvocationCounter;
           OutCmd.OriginID          := _response.OriginID;
           // set timeout
           SetTimeout(PT := _timeoutCmd, rTimer := _timerCmd);
           // inc step counter
          _stepCmd := _stepCmd + 1; 
				 END_IF
       END_IF

  03: Done := TRUE;

      IF ( NOT Execute)
      THEN
        // reset step counter
       _stepCmd := 0;
        // reset done flag
        Done := FALSE;
        // reset busy flag
        Busy := FALSE;    
        // reset buffered flag
        CommandBuffered := FALSE;  
			END_IF     
ELSE
  // invalid step
  ErrorID := RobotLibraryEvents.ERR_INVALID_STEP;
END_CASE


// Cancel 
IF (Busy) AND (NOT Execute) 
THEN
  // try to remove cmd
  AxesGroup.Acyclic.ActiveCommandRegister.RemoveCmd(_uniqueID);
END_IF


// Reset FB
IF ( NOT Execute )
THEN
 _uniqueId        := 0;
 _stepCmd         := 0;
  Done            := FALSE;
  Busy            := FALSE;
  CommandBuffered := FALSE;
  Error           := FALSE;
  ErrorID         := 0;
  WarningID       := 0;
  InfoID          := 0;  
END_IF

InterfaceAsPlainText.xhtml:
METHOD PROTECTED OnCall
VAR_IN_OUT
  /// Robot assignment of function
  AxesGroup : AxesGroup; 
END_VAR

ST.xhtml:
// Table 6-26: Received CMD payload (RC to PLC) of "EnableRobot"
// --------------------------
// Header
// --------------------------
// Byte 00 : USINT - ParSeq | State     
// Byte 01 : SINT  - AlarmMessageSeverity    
// Byte 02 : UINT  - AlarmMessageCode HB
// Byte 03 :       - AlarmMessageCode LB
// --------------------------
// Datablock
// --------------------------
// Byte 04 : USINT - InvocationCounter
// Byte 05 : SINT  - Reserved
// Byte 06 : INT   - OriginID HB
// Byte 07 : INT   - OriginID LB
// --------------------------

// call base implementation to parse the header from payload buffer
SUPER^.ParseResponseData(ResponseData);

// copy parsed header to response
_response.ParSeq               := _rspHeader.ParSeq;
_response.State                := _rspHeader.State;
_response.AlarmMessageSeverity := _rspHeader.AlarmMessageSeverity;
_response.AlarmMessageCode     := _rspHeader.AlarmMessageCode;

// Get InvocationCounter
_response.InvocationCounter    := ResponseData.GetUsint();
// Get Reserve
_response.Reserve              := ResponseData.GetSint();
// Get OriginID
_response.OriginID             := ResponseData.GetInt();

InterfaceAsPlainText.xhtml:
METHOD ParseResponsePayload : DINT
VAR_INPUT
  /// Response data
  ResponseData : RecvData;
END_VAR

