VAR_INPUT
  /// Start of the command at the rising edge
  Execute                : BOOL;
  /// Relates to ListenerID >0:
  ProcessingMode         : ProcessingMode;
  /// Index of assigned conveyor
  ConveyorNo             : USINT;
  /// Relates to ListenerID = 0:
  /// Define initial work piece position relative to the conveyor belt origin:
  /// • Origin (X, Y, Z in mm)
  /// • Orientation (RX, RY, RZ in degree)
  /// For more information refer to chapter 5.5.14.
  InitObjectPosition     : ARRAY[0..5] OF REAL;
  /// Relates to ListenerID = 0:
  /// Travel distance of assigned UCS between work piece detection (InitObjectPosition) and execution of this command.
  TrackingOffset         : REAL;
  /// Relates to ListenerID >0
  /// Start index of real registers containing InitObjectPosition for trigger-based execution.
  StartIndexInitPosition : INT;
  /// Define the index number of the UCS that is assigned to the conveyor by the function "ConveyorTracking".
  /// For more information refer to chapter 5.5.14.
  FrameNo                : USINT;
  /// Relates to ListenerID >0
  /// Index of real register containing TrackingOffset for trigger-based execution
  IndexTrackingOffset    : USINT;
  /// ID of associated trigger function:
  /// • 0: No Trigger (default): No trigger related behavior
  /// • >0: Trigger: o Start executing, when the trigger function with the identical EmitterID is triggered.
  /// Always positive.
  /// For more information, see chapter 5.5.12 Triggers
  ListenerID             :  SINT;
END_VAR

VAR_OUTPUT
  /// The command has been completed successfully
  Done                   : BOOL;
  /// FB is being processed
  Busy                   : BOOL;
  /// Command is transferred and confirmed by the RC
  CommandBuffered        : BOOL;
  /// Receiving of input parameter values has been acknowledged by RC
  ParameterAccepted      : BOOL;
  /// Unique system-generated ID of the "Action" when the function is triggered.
  /// • >0: The "Action" is started by the trigger funcrion with identical FollowID.
  /// • <0: The "Action" is stopped by the trigger function with identical FollowID.
  /// For more information see chapter 5.5.12.4 EmitterID, ListenerID, FollowID and OriginID
  OriginID               : INT;
  /// Relates to ListenerID >0
  /// Number of successful trigger-based command invocations - For more information refer TO chapter 5.5.12.4
  InvocationCounter      : USINT;
END_VAR

METHOD PROTECTED OnCall
VAR_IN_OUT
  /// Robot assignment of function
  AxesGroup         : AxesGroup; 
END_VAR

/// Synchronize robot with conveyor
FUNCTION_BLOCK SyncToConveyor EXTENDS RobotLibraryBaseFB
// ------------------------------------------------------------------------- 
//  SRCI Robot Library                                                
// ------------------------------------------------------------------------- 
//                                                                           
//  Object:      SyncToConveyor                                       
//  Author:      Thorsten Brach                                                
//  Date:        2024-06-01
//
//  Description:                                              
//   
//                                                                           
//  Copyright: 
//    (C) 2024 Thorsten Brach. All rights reserved                                                                         
//                                                                           
//  Disclaimer:                                                                           
//    This project is provided without any guarantee and can be used for   
//    private and commercial purposes. Any commercial use is at the user’s 
//    own risk and responsibility.
//
// -------------------------------------------------------------------------
VAR_INPUT
  /// Start of the command at the rising edge
  Execute            : BOOL;
  /// Index of assigned conveyor
  ConveyorNo         : USINT;
  /// Define the index number of the UCS that will be assigned to the conveyor.
  /// For more information refer to chapter 5.5.14
  FrameNo            : USINT;
  /// Defines when synchronization starts
  SyncInMode         : SyncInMode;
  /// Relates to "SyncInMode" "After distance" (1) and "After time" (2): Parameter for the synchronization
  /// • "After distance" (1) -  Distance in mm
  /// • "After time" (2) -  Time in ms
  SyncInParameter    : REAL;
  /// Define the maximum adjustment velocity for the robot to catch up to the conveyor belt.
  /// The velocity must be greater than the velocity of the conveyor belt.
  /// • 0 (default): no limit - No conveyor tracking specific limit
  ///                         - RC internal limit applies
  /// • >0: use limit         - Conveyor tracking specific limit applies
  MaxVelocity        : REAL;
  /// Define the maximum adjustment acceleration for the robot to catch up to the conveyor belt.
  /// • 0 (default): no limit - No conveyor tracking specific limit
  ///                         - RC internal limit applies
  /// • >0: use limit         - Conveyor tracking specific limit applies
  MaxAcceleration    : REAL;
  /// ID of associated trigger function:
  /// • 0: No Trigger (default): No trigger related behavior
  /// • >0: Trigger:  Start executing, when the trigger function with the identical EmitterID is triggered.
  /// Always positive.
  /// For more information, see chapter 5.5.12 Triggers
  ListenerID         : SINT;
END_VAR

VAR_OUTPUT
  /// FB is being processed
  Busy               : BOOL;
  /// Command is transferred and confirmed by the RC
  CommandBuffered    : BOOL;
  /// TRUE, while SyncToConveyor is enabled and processing input parameters
  Enabled            : BOOL;
  /// The command takes control of the motion of the according axis group
  Active             : BOOL;
  /// The command was aborted by another command.
  CommandAborted     : BOOL;
  /// Receiving of input parameter values has been acknowledged by RC
  ParameterAccepted  : BOOL;
  /// The TCP is in synchronization with the object on the conveyor belt.
  InSync             : BOOL;
  /// Unique system-generated ID of the "Action" when the function is triggered.
  /// • >0: The "Action" is started by the trigger function with identical FollowID.
  /// • <0: The "Action" is stopped by the trigger function with identical FollowID.
  /// For more information see chapter 5.5.12.4 EmitterID, ListenerID, FollowID and OriginID
  OriginID           : INT;
  /// Relates to ListenerID >0
  /// Number of successful trigger -based command invocations
  /// For more information refer to chapter 5.5.12.4.
  InvocationCounter  : USINT;
END_VAR

METHOD PROTECTED OnCall
VAR_IN_OUT
  /// Robot assignment of function
  AxesGroup         : AxesGroup; 
END_VAR

/// Enables the RC to apply user defined force/ torque through RA’s TCP movement
FUNCTION_BLOCK ForceControl EXTENDS RobotLibraryBaseFB
// ------------------------------------------------------------------------- 
//  SRCI Robot Library                                                
// ------------------------------------------------------------------------- 
//                                                                           
//  Object:      ForceControl                                       
//  Author:      Thorsten Brach                                                
//  Date:        2024-06-01
//
//  Description:                                              
//   
//                                                                           
//  Copyright: 
//    (C) 2024 Thorsten Brach. All rights reserved                                                                         
//                                                                           
//  Disclaimer:                                                                           
//    This project is provided without any guarantee and can be used for   
//    private and commercial purposes. Any commercial use is at the user’s 
//    own risk and responsibility.
//
// -------------------------------------------------------------------------
VAR_INPUT
  /// Start of the command at the rising edge
  Execute            : BOOL;
  /// Defines type of reference of Force and Torque (at least one option must be supported)
  ReferenceType      : ReferenceType;
  /// Relates to ConnectionMode 1 and 2 (Sensor connected to PLC).
  /// Currently applied force and torque detected by sensor according to ReferenceType, ToolNo and FrameNo.
  /// • [0]: X-Axis
  /// • [1]: Y-Axis
  /// • [2]: Z- Axis
  /// • [3]: Rotation around X-Axis (RX)
  /// • [4]: Rotation around Y-Axis (RY)
  /// • [5]: Rotation around Z- Axis (RZ)
  SensorValue        : ARRAY[0..5] OF REAL;
  /// Relates to ReferenceType 0 (Tool) and 1 (Frame):
  /// Force and torque to be applied by robot arm according to ReferenceType, ToolNo and FrameNo:
  /// • [0]: X-Axis
  /// • [1]: Y-Axis
  /// • [2]: Z- Axis
  /// • [3]: Rotation around X-Axis (RX)
  /// • [4]: Rotation around Y-Axis (RY)
  /// • [5]: Rotation around Z- Axis (RZ)
  /// Set TargetValue <>-99999.9999 (default) to define setpoint
  TargetValue        : ARRAY[0..5] OF REAL;
  /// Index of tool
  /// •      0: Flange (default)
  /// • 1..254: Tool frames
  ToolNo             : USINT;
  /// Define the index number of the UCS that will be assigned to the conveyor.
  /// For more information refer to chapter 5.5.14
  FrameNo            : USINT;
  /// Specifies encoder connection setup according to Table 6-713 (at least one option must be supported) 
  ConnectionMode     : SensorConnectionMode;
  /// Position of sensor relative to flange (FixedSensor FALSE) or relative to WCS (FixedSensor TRUE)
  /// • [0]: X-Axis
  /// • [1]: Y-Axis
  /// • [2]: Z- Axis
  /// • [3]: Rotation around X-Axis (RX)
  /// • [4]: Rotation around Y-Axis (RY)
  /// • [5]: Rotation around Z- Axis (RZ)
  SensorFrame        : ARRAY[0..5] OF REAL;
  /// Index of payload data containing mass, center of gravity, orientation and inertia relative to SensorFrame.
  /// Required to calibrate system for setting up gravity compensation and sensor offset.
  CalibrationData    : USINT;
  /// Tolerance of TargetValue within which currently applied force/torque will return ForceStatus "Specified force/torque reached" TRUE.
  TargetWindow       : REAL;
  /// Define axes whose position may be adjusted by the robot to apply specified force according to Table 6-711.
  CompliantAxes      : BYTE;
  /// Maximum allowed TCP velocity for compliant axes in % of nominal velocity.
  /// •   0% (default): no limit - RC internal limit applies
  /// •  >0% use limit -  Specified limit applies
  /// • 100% use maximal reference velocity
  /// For more information on nominal dynamics see chapter 5.5.7 Robot dynamics 
  MaxVelocity        : REAL;
  /// Maximum allowed cartesian deviation according to ReferenceType, ToolNo and FrameNo.
  /// Set 0 (default) to not specify a limit in this axis direction.
  /// • [0]: Deviation in X-Direction in mm
  /// • [1]: Deviation in Y- direction in mm
  /// • [2]: Deviation in Z- direction in mm
  /// • [3]: Deviation in RX- direction in °
  /// • [4]: Deviation in RY- direction in °
  /// • [5]: Deviation in RZ- direction in °
  MaxDeviation       : ARRAY [0..5] OF REAL;
  /// Defines reaction in case ForceControl reports error during its execution
  /// Processing of active move command is continued without active ForceControl
  ErrorReaction      : ErrorReaction;
  /// Relates to ErrorReaction 0 (Abort and move). Defines type of reference coordinate system of ErrorVector
  ErrorReferenceType : ReferenceType;
  /// Relates to ErrorReaction 0 (Abort and move). Vector defining robot’s movement in case of
  /// incoming error according to ErrorReferenceType, ErrorToolNo and ErrorFrameNo.
  /// • [0]: X-Axis
  /// • [1]: Y-Axis
  /// • [2]: Z- Axis
  /// • [3]: Rotation around X-Axis (RX)
  /// • [4]: Rotation around Y-Axis (RY)
  /// • [5]: Rotation around Z-Axis (RZ)
  ErrorVector        : ARRAY [0..5] OF REAL;
  /// Relates to ErrorReaction 0 (Abort and move). Index of tool.
  /// • 0 (default): Flange
  /// • 1..254: Tool frames
  ErrorToolNo        : USINT;
  /// Relates to ErrorReferenceType 1 (Frame)
  /// •      0: WCS (default)
  /// • 1..254: User frames
  ErrorFrameNo       : USINT;
  /// Defines sensor positioning:
  /// • FALSE (default): Sensor mounted on flange
  /// • TRUE: Sensor fixed
  FixedSensor        : BOOL;
END_VAR

VAR_OUTPUT
  /// TRUE, while the following outputs return valid values:
  /// - ForceStatus
  Valid              : BOOL;
  /// FB is being processed
  Busy               : BOOL;
  /// Command is transferred and confirmed by the RC
  CommandBuffered    : BOOL;
  /// Receiving of input parameter values has been acknowledged by RC
  ParameterAccepted  : BOOL;
  /// Current status of ForceControl and ForceLimit (chapter 6.5.25) according to Table 6-712
  ForceStatus        : ForceStatus;
END_VAR

METHOD PROTECTED OnCall
VAR_IN_OUT
  /// Robot assignment of function
  AxesGroup         : AxesGroup; 
END_VAR

/// Commands specified reaction from RA when defined force/torque detected
FUNCTION_BLOCK ForceLimit EXTENDS RobotLibraryBaseFB
// ------------------------------------------------------------------------- 
//  SRCI Robot Library                                                
// ------------------------------------------------------------------------- 
//                                                                           
//  Object:      ForceLimit                                       
//  Author:      Thorsten Brach                                                
//  Date:        2024-06-01
//
//  Description:                                              
//   
//                                                                           
//  Copyright: 
//    (C) 2024 Thorsten Brach. All rights reserved                                                                         
//                                                                           
//  Disclaimer:                                                                           
//    This project is provided without any guarantee and can be used for   
//    private and commercial purposes. Any commercial use is at the user’s 
//    own risk and responsibility.
//
// -------------------------------------------------------------------------
VAR_INPUT
  /// Start of the command at the rising edge
  Execute            : BOOL;
  /// Defines type of reference of Force and Torque (at least one option must be supported)
  ReferenceType      : ReferenceType;
  /// Relates to ConnectionMode 1 and 2 (Sensor connected to PLC).
  /// Currently applied force and torque detected by sensor according to ReferenceType, ToolNo and FrameNo.
  /// • [0]: X-Axis
  /// • [1]: Y-Axis
  /// • [2]: Z- Axis
  /// • [3]: Rotation around X-Axis (RX)
  /// • [4]: Rotation around Y-Axis (RY)
  /// • [5]: Rotation around Z- Axis (RZ)
  SensorValue        : ARRAY[0..5] OF REAL;
  /// Index of tool
  /// •      0: Flange (default)
  /// • 1..254: Tool frames
  ToolNo             : USINT;
  /// Define the index number of the UCS that will be assigned to the conveyor.
  /// For more information refer to chapter 5.5.14
  FrameNo            : USINT;
  /// Maximal allowed force and torque detected by the sensor according to ReferenceType and ToolNo/FrameNo before robot reacts according to ReactionMode.
  /// Set value <>0 (default) to monitor coordinate axes
  /// • [0]: X-Axis
  /// • [1]: Y-Axis
  /// • [2]: Z- Axis
  /// • [3]: Rotation around X-Axis (RX)
  /// • [4]: Rotation around Y-Axis (RY)
  /// • [5]: Rotation around Z- Axis (RZ)
  ForceLimit        : ARRAY[0..5] OF REAL;
  /// Specifies encoder connection setup according to Table 6-713 (at least one option must be supported) 
  ConnectionMode    : SensorConnectionMode;
  /// Position of sensor relative to flange (FixedSensor FALSE) or relative to WCS (FixedSensor TRUE)
  /// • [0]: X-Axis
  /// • [1]: Y-Axis
  /// • [2]: Z- Axis
  /// • [3]: Rotation around X-Axis (RX)
  /// • [4]: Rotation around Y-Axis (RY)
  /// • [5]: Rotation around Z- Axis (RZ)
  SensorFrame        : ARRAY[0..5] OF REAL;
  /// Index of payload data containing mass, center of gravity, orientation and inertia relative to SensorFrame.
  /// Required to calibrate system for setting up gravity compensation and sensor offset.
  CalibrationData    : USINT;
  /// Defines sensor positioning:
  /// • FALSE (default): Sensor mounted on flange
  /// • TRUE: Sensor fixed
  FixedSensor        : BOOL;
  /// ID of Action that will be executed when this command is active
  /// • >0: Start Action        - Start executing the Action function with the identical ListenerID.
  /// • <0: Stop Action         - Stop executing the Action function with the identical ListenerID.
  /// •  0: Undefined (default) - If no EmitterID is defined, the function returns an error message
  /// For more information see chapter 5.5.12.4
  EmitterID          : SINT;  
END_VAR

VAR_OUTPUT
  /// TRUE, while the following outputs return valid values
  Valid              : BOOL;
  /// FB is being processed
  Busy               : BOOL;
  /// Command is transferred and confirmed by the RC
  CommandBuffered    : BOOL;
  /// Receiving of input parameter values has been acknowledged by RC
  ParameterAccepted  : BOOL;
  /// Current status of ForceControl and ForceLimit (chapter 6.5.25) according to Table 6-712
  ForceStatus        : ForceStatus;
  /// Relates to EmitterID <> 0: Unique system-generated ID of the trigger function when the function is called by user.
  /// For more information see chapter 5.5.12.4.
  FollowID           : DINT;
END_VAR

METHOD PROTECTED OnCall
VAR_IN_OUT
  /// Robot assignment of function
  AxesGroup         : AxesGroup; 
END_VAR

/// Read actual force/torque at TCP
FUNCTION_BLOCK ReadActualForce EXTENDS RobotLibraryBaseFB
// ------------------------------------------------------------------------- 
//  SRCI Robot Library                                                
// ------------------------------------------------------------------------- 
//                                                                           
//  Object:      ReadActualForce                                       
//  Author:      Thorsten Brach                                                
//  Date:        2024-06-01
//
//  Description:                                              
//   
//                                                                           
//  Copyright: 
//    (C) 2024 Thorsten Brach. All rights reserved                                                                         
//                                                                           
//  Disclaimer:                                                                           
//    This project is provided without any guarantee and can be used for   
//    private and commercial purposes. Any commercial use is at the user’s 
//    own risk and responsibility.
//
// -------------------------------------------------------------------------
VAR_INPUT
  /// Start of the command at the rising edge
  Execute            : BOOL;
  /// Processing Mode
  ProcessingMode      : ProcessingMode;
  /// Defines type of reference of Force and Torque (at least one option must be supported )
  ReferenceType       : ReferenceType;
  /// Index of tool
  /// •      0: Flange (default)
  /// • 1..254: Tool frames
  ToolNo             : USINT;
  /// Define the index number of the UCS that will be assigned to the conveyor.
  /// For more information refer to chapter 5.5.14
  FrameNo            : USINT;
  /// Index of payload data containing mass, center of gravity, orientation and inertia relative to SensorFrame.
  /// Required to calibrate system for setting up gravity compensation and sensor offset.
  CalibrationData    : USINT;
  /// ID of associated trigger function:
  /// • 0: Immediately (default) - Start executing this function immediately.
  /// • >0: Trigger -  Start executing, when the trigger function with the identical EmitterID is triggered.
  /// Always positive.
  /// For more information, see chapter 5.5.12 Triggers
  ListenerID         : SINT;

END_VAR

VAR_OUTPUT
  /// TRUE, when command has been completed successfully.
  Done               : BOOL;
  /// TRUE, while the following outputs return valid values:
  /// - ActualForce
  Valid              : BOOL;
  /// FB is being processed
  Busy               : BOOL;
  /// Command is transferred and confirmed by the RC
  CommandBuffered    : BOOL;
  /// Receiving of input parameter values has been acknowledged by RC
  ParameterAccepted  : BOOL;
  /// Currently detected force and torque according to ReferenceType, ToolNo and FrameNo.
  /// • [0]: X-Axis
  /// • [1]: Y-Axis
  /// • [2]: Z- Axis
  /// • [3]: Rotation around X-Axis (RX)
  /// • [4]: Rotation around Y-Axis (RY)
  /// • [5]: Rotation around Z- Axis (RZ)
  ActualForce        : ARRAY[0..5] OF REAL;
  /// Unique system-generated ID of the "Action" when the function is triggered.
  /// • >0: The "Action" is started by the trigger function with identical FollowID.
  /// • <0: The "Action" is stopped by the trigger function with identical FollowID.
  /// For more information see chapter 5.5.12.4 EmitterID, ListenerID, FollowID and OriginID
  OriginID           : INT;
  /// Relates to ListenerID >0
  /// Number of successful trigger -based command invocations
  /// For more information refer to chapter 5.5.12.4.
  InvocationCounter  : USINT;

END_VAR

METHOD PROTECTED OnCall
VAR_IN_OUT
  /// Robot assignment of function
  AxesGroup         : AxesGroup; 
END_VAR

