/// Turn on/off the collision detection
FUNCTION_BLOCK CollisionDetection EXTENDS RobotLibraryBaseFB
// ------------------------------------------------------------------------- 
//  SRCI Robot Library                                                
// ------------------------------------------------------------------------- 
//                                                                           
//  Object:      CollisionDetection                                       
//  Author:      Thorsten Brach                                                
//  Date:        2024-06-01
//
//  Description:                                              
//   
//                                                                           
//  Copyright: 
//    (C) 2024 Thorsten Brach. All rights reserved                                                                         
//                                                                           
//  Disclaimer:                                                                           
//    This project is provided without any guarantee and can be used for   
//    private and commercial purposes. Any commercial use is at the user’s 
//    own risk and responsibility.
//
// -------------------------------------------------------------------------
VAR_INPUT
  /// Start of the command at the rising edge
  Execute            : BOOL;
  /// Processing mode
  ProcessingMode     : ProcessingMode;
  ///  Defines the robot behavior after detecting the collision (at least one option must be supported):
  ReactionMode       : CollisionReactionMode;
  /// Set TRUE to activate and FALSE to deactivate "CollisionDetection".
  /// Default: TRUE
  ActivateMonitoring : BOOL;
  /// Defines the type of threshold setting (at least one option must be supported)
  ThresholdMode      : ThresholdMode;
  /// Defines the level of sensitivity of all axes.
  /// • 0..99: Sensitivity lower than robot vendor default. 
  ///          At minimum value "0" there is no sensitivity. The lower the value, the lower the sensitivity at the collision detection.
  /// • 100 (default): The value is aligned with the robot vendor-specific default value.
  /// • 101..200: Sensitivity higher than robot vendor default. At maximum value "200" highest possible sensitivity at the collision detection.
  Sensitivity        : REAL;
  /// Defines the level of sensitivity of the respective axis.
  /// Values:
  /// • 0..99: Sensitivity lower than robot vendor default. At minimum value "0" there is no sensitivity and collision detection for the respective axis. 
  ///          The lower the value, the lower the sensitivity at the collision detection.
  /// • 100 (default): The value is aligned with the robot vendor-specific default value.
  /// • 101..200: Sensitivity higher than robot vendor default. At maximum value "200" highest possible sensitivity at the collision detection.
  /// Index:
  /// • [0]: J1 First joint of the robot.
  /// • [1]: J2 Second joint of the robot.
  /// • [2] J3 Third joint of the robot.
  /// • [3] J4 Forth joint of the robot.
  /// • [4] J5 Fifth joint of the robot.
  /// • [5] J6 Six joint of the robot.
  /// • [6] J7 Seventh joint of the robot, if willbe used.
  SensitivityAxis    : ARRAY[0..6] OF REAL;
  /// Relates to "ThresholdMode" Manual. Defines the threshold of the torque for each robot axis. If the threshold is exceeded, the RA stops.
  /// Index:
  /// • [0] J1 First joint of the robot.
  /// • [1] J2 Second joint of the robot.
  /// • [2] J3 Third joint of the robot.
  /// • [3] J4 Forth joint of the robot.
  /// • [4] J5 Fifth joint of the robot.
  /// • [5] J6 Six joint of the robot.
  /// • [6] J7 Seventh joint of the robot, if will be used
  LimitAxis          : ARRAY[0..6] OF REAL;
  /// Relates to "LimitAxis". Defines the unit of the threshold.
  /// • [0]: Percentage (%) (default) O
  /// • [1]: Newton meter (Nm). O
  /// • [2] Milliampere (mA).
  UnitLimitAxis      : USINT;
  /// Defines the target sequence in which the command will be executed
  SequenceFlag       : SequenceFlag;
END_VAR
VAR_OUTPUT
  /// The command has been completed successfully
  Done               : BOOL;
  /// FB is being processed
  Busy               : BOOL;
  /// Command is transferred and confirmed by the RC
  CommandBuffered    : BOOL;
  /// The command was aborted by another command.
  CommandAborted     : BOOL;
  /// TRUE, while command is interrupted during execution and can be continued
  CommandInterrupted : BOOL;  
END_VAR

ST.xhtml:
// compare memory 
CheckParameterChanged := ( MemCmp(ADR(ParCmd), ADR(_parCmd), SIZEOF(ParCmd)) = RobotLibraryConstants.OK );

IF ( CheckParameterChanged )
THEN  
  // update internal copy of parameters 
 _parCmd := ParCmd;
  // inc parameter sequence
 _command.ParSeq := _command.ParSeq + 1;
  // update command data  
  CommandData := CreateCommandData(); // ( Access via reference to rCommandFB in ACR )
END_IF

// Table 6-25: Sent CMD payload (PLC to RC) of "EnableRobot"

// --------------------------
// Header
// --------------------------
// Byte 00 : UINT  - Type HB     
// Byte 01 :       - Type LB    
// Byte 02 : USINT - Reserve | ExecutionMode
// Byte 03 : USINT - ParSeq  | Priority
// --------------------------
// Datablock
// --------------------------
// Byte 04 : BOOL  - Enable
// Byte 05 : BOOL  - HoldToRun
// Byte 06 : USINT - StepMode   
// Byte 07 : BOOL  - ManualStep
// --------------------------

// set command parameter 
_command.CmdTyp    :=  CmdType.EnableRobot;
_command.ExecMode  :=  ExecutionMode.PARALLEL;
_command.ParSeq    := _command.ParSeq;
_command.Priority  :=  PriorityLevel.NORMAL;
_command.Enable    :=  Enable;
_command.HoldToRun := _parCmd.HoldToRun;
_command.StepMode  := _parCmd.StepMode;
_command.ManualStep:= _parCmd.ManualStep;


// copy command data to header
_cmdHeader := _command;
// call base implementation to copy header to payload buffer
SUPER^.CreateCommandData();

// add command.Enable
CreateCommandData.AddBool(_command.Enable);
// add command.StepMode
CreateCommandData.AddByte(_command.StepMode);
// add command.HoldToRun
CreateCommandData.AddBool(_command.HoldToRun);
// add command.ManualStep
CreateCommandData.AddBool(_command.ManualStep);

// building rising and falling edges
_enable_R( CLK := Enable);
_enable_F( CLK := Enable);

CASE _stepCmd OF

  00:  IF ( _enable_R.Q ) OR (_enable_F.Q) 
       THEN
         // set busy flag
         Busy := TRUE;
         // Reset command outputs
         MemSet(ADR(OutCmd),0,SIZEOF(OutCmd));         
         // apply command parameter
        _parCmd := ParCmd;
         // init parameter sequence
        _command.ParSeq := 1;
         // set timeout
         SetTimeout(PT := _timeoutCmd, rTimer := _timerCmd);
         // inc step counter
        _stepCmd := _stepCmd + 1; 
	     END_IF

  01:  // create command data
       CommandData := CreateCommandData();
       // Add command to active command register
      _uniqueID := AxesGroup.Acyclic.ActiveCommandRegister.AddCmd( rCommandFB := THIS^ );     
       // set timeout
       SetTimeout(PT := _timeoutCmd, rTimer := _timerCmd);
       // inc step counter
      _stepCmd := _stepCmd + 1; 

  02:  // Wait for responce received
       IF ( _responseReceived )
       THEN
         // reset response received flag
        _responseReceived := FALSE;

         CASE  _response.State 
         OF
           CmdMessageState.BUFFERED    : CommandBuffered    := TRUE;
           CmdMessageState.ERROR       : Error              := TRUE;
				 END_CASE

         // Done, Aborted or Error ?
         IF (_response.State >= CmdMessageState.DONE )
         THEN
           // Update results
           OutCmd.Enabled     := _response.Enabled;
           // set timeout
           SetTimeout(PT := _timeoutCmd, rTimer := _timerCmd);
           // inc step counter
          _stepCmd := _stepCmd + 1; 
				 END_IF
       END_IF

  03: // reset step counter
     _stepCmd := 0;
      // reset busy flag
      Busy := FALSE;    
      // reset buffered flag
      CommandBuffered := FALSE;  
ELSE
  // invalid step
  ErrorID := RobotLibraryEvents.ERR_INVALID_STEP;
END_CASE



// Reset FB
IF ( FALSE ) {warning 'ToDo: find condition'}
THEN
 _uniqueId           := 0;
 _stepCmd            := 0;
  Busy               := FALSE;
  CommandBuffered    := FALSE;
  Error              := FALSE;
  ErrorID            := 0;
  WarningID          := 0;
  InfoID             := 0;  
END_IF

InterfaceAsPlainText.xhtml:
METHOD PROTECTED OnCall
VAR_IN_OUT
  /// Robot assignment of function
  AxesGroup : AxesGroup; 
END_VAR

ST.xhtml:
// Table 6-26: Received CMD payload (RC to PLC) of "EnableRobot"
// --------------------------
// Header
// --------------------------
// Byte 00 : USINT - ParSeq | State     
// Byte 01 : SINT  - AlarmMessageSeverity    
// Byte 02 : UINT  - AlarmMessageCode HB
// Byte 03 :       - AlarmMessageCode LB
// --------------------------
// Datablock
// --------------------------
// Byte 04 : BOOL  - Enabled
// --------------------------

// call base implementation to parse the header from payload buffer
SUPER^.ParseResponseData(ResponseData);

// copy parsed header to response
_response.ParSeq               := _rspHeader.ParSeq;
_response.State                := _rspHeader.State;
_response.AlarmMessageSeverity := _rspHeader.AlarmMessageSeverity;
_response.AlarmMessageCode     := _rspHeader.AlarmMessageCode;

// Get Response.Enabled
_response.Enabled := ResponseData.GetBool();

InterfaceAsPlainText.xhtml:
METHOD ParseResponsePayload : DINT
VAR_INPUT
  /// Response data
  ResponseData : RecvData;
END_VAR
VAR
 /// index for payload buffer
 _idx : DINT;  
END_VAR

