/// Return to path left during active interrupt
FUNCTION_BLOCK ReturnToPrimary EXTENDS RobotLibraryBaseFB
// ------------------------------------------------------------------------- 
//  SRCI Robot Library                                                
// ------------------------------------------------------------------------- 
//                                                                           
//  Object:      ReturnToPrimary                                       
//  Author:      Thorsten Brach                                                
//  Date:        2024-06-01
//
//  Description:                                              
//   
//                                                                           
//  Copyright: 
//    (C) 2024 Thorsten Brach. All rights reserved                                                                         
//                                                                           
//  Disclaimer:                                                                           
//    This project is provided without any guarantee and can be used for   
//    private and commercial purposes. Any commercial use is at the user’s 
//    own risk and responsibility.
//
// -------------------------------------------------------------------------
VAR_INPUT
  /// Start of the command at the rising edge
  Execute            : BOOL;
  /// Defines target position when function is executed:
  /// • 0: Interrupt position (default) - Returns to position active,  when interrupt was executed
  /// • 1: End position                 - Returns to target position of interrupted segment
  ReturnMode         : BOOL;
  /// TCP velocity in % of nominal velocity
  /// •  <0%: (default) - Use default velocity
  /// •   0%:           - Use internal minimal velocity
  /// • 100%:           - Use maximal reference velocity
  /// See chapter 5.5.7 Robot dynamics
  VelocityRate       : REAL;
  /// Acceleration for movement in % of nominal acceleration
  /// •  <0%: (default) - Use default acceleration
  /// •   0%:           - Use internal minimal acceleration
  /// • 100%:           - Use maximal reference acceleration
  /// See chapter 5.5.7 Robot dynamics
  AccelerationRate   : REAL;
  /// Deceleration for movement in % of nominal deceleration
  /// •  <0%: (default) - Use default deceleration 
  /// •   0%:           - Use internal minimal deceleration
  /// • 100%:           - Use maximal reference deceleration
  /// See chapter 5.5.7 Robot dynamics
  DecelerationRate   : REAL;
  /// Jerk of the movement in % of nominal jerk
  /// •  <0%: (default) - Use default jerk
  /// •   0%:           - Use internal minimal jerk
  /// • 100%:           - Use maximal reference jerk
  /// See chapter 5.5.7 Robot dynamics
  JerkRate           : REAL;
  /// Index of tool
  /// •      0: Flange (default)
  /// • 1..254: Tool frames
  ToolNo             : USINT;
  /// Index of frame
  /// •      0: WCS (default)
  /// • 1..254: User frames
  FrameNo            : USINT;
  /// Parameter is used if it is greater than 0 (default):
  /// Maximum allowed distance between current position and target position according to ReturnMode.
  /// If calculated distance is beyond specified Limit, RC returns an error
  DistanceLimit      : REAL;
  /// Defines type of command movement. One of the optional modes must be supported
  TrajectoryMode     : TrajectoryMode;
  /// Parameter is used if it is greater than 0 (default)
  /// • Velocity input is ignored
  /// • Parameter defines the time for the movement to reach the target position
  /// Error is sent by the RC if the time cannot be kept
  MoveTime           : TIME;
  /// Set TRUE to deactivate comparison of
  /// • ToolData and FrameData
  /// • ToolNo and PrimaryPosToolNo
  /// • FrameNo and PrimaryPosFrameNo
  AllowDifferences   : BOOL;
END_VAR

VAR_OUTPUT
  /// The command has been completed successfully
  Done               : BOOL;
  /// FB is being processed
  Busy               : BOOL;
  /// Command is transferred and confirmed by the RC
  CommandBuffered    : BOOL;
  /// The command takes control of the motion of the according axis group
  Active             : BOOL;
  /// The command was aborted by another command.
  CommandAborted     : BOOL;
  /// TRUE, while command is interrupted during execution and can be continued
  CommandInterrupted : BOOL;
  /// Distance-to-go of the current job.
  /// • -1: No valid value because move command is not yet active (valid values are pending) or not supported by RC
  /// • >0: Actual distance between current and target position
  /// •  0: Target position reached
  RemainingDistance  : REAL;
  /// Percentage of already traversed distance of current job.
  /// If not supported : • -1
  Progress           : REAL;
  /// Tool index of target position of ReturnToPrimary
  /// • 0: Flange
  /// • 1..254: Tool frames
  PrimaryPosToolNo   : USINT;
  /// Frame index of target position of ReturnToPrimary
  /// • 0: WCS
  ///• 1..254: User frames
  PrimaryPosFrameNo  : USINT;
END_VAR

METHOD PROTECTED OnCall
VAR_IN_OUT
  /// Robot assignment of function
  AxesGroup         : AxesGroup; 
END_VAR

/// Transform a defined position in space
FUNCTION_BLOCK ShiftPosition EXTENDS RobotLibraryBaseFB
// ------------------------------------------------------------------------- 
//  SRCI Robot Library                                                
// ------------------------------------------------------------------------- 
//                                                                           
//  Object:      ShiftPosition                                       
//  Author:      Thorsten Brach                                                
//  Date:        2024-06-01
//
//  Description:                                              
//   
//                                                                           
//  Copyright: 
//    (C) 2024 Thorsten Brach. All rights reserved                                                                         
//                                                                           
//  Disclaimer:                                                                           
//    This project is provided without any guarantee and can be used for   
//    private and commercial purposes. Any commercial use is at the user’s 
//    own risk and responsibility.
//
// -------------------------------------------------------------------------
VAR_INPUT
  /// Start of the command at the rising edge
  Execute                   : BOOL;
  /// Define which method should be used to transform the position:
  Mode                      : TransformMode;
  /// Absolute coordinates of the position that will be transformed in the selected coordinate systems (see input parameter CoordinateSystem).
  Position                  : RobotCartesianPosition;
  /// Defines the reference frame ID to which the "Position" that will be transformed is relative.
  /// • 0: WCS (default)
  /// • 1..254: User frames
  FrameNo                   : USINT;
  /// Defines the reference frame ID to which the "TransformedPosition" should be relative.
  /// • 0: WCS (default)
  /// • 1..254: User frames
  TargetFrameNo             : USINT;
  /// Defines the frame data relevant for the transformation of the "Position". Relates to all "Mode" settings. 
  /// The parameter includes the Frame ID to which the origin of the shifting and rotation is relative and the cartesian position of the origin
  TransformationParameter_1 : FrameData;
  /// Relates to all "Mode" settings. Defines the enabled axis or plane as reference straight line or reference plain for the transformation
  TransformationParameter_2 : ReferenceElement;
  /// Defines the angle of rotation around the defined straight line. 
  /// Related to "Mode" with the setting "Rotate around Straight Line".
  RotationAngle             : REAL;
END_VAR
VAR_OUTPUT
  /// The command has been completed successfully
  Done                      : BOOL;
  /// FB is being processed
  Busy                      : BOOL;
  /// Command is transferred and confirmed by the RC
  CommandBuffered           : BOOL;
  /// Tool index
  TransformedPosition       : RobotCartesianPosition;
END_VAR

METHOD PROTECTED OnCall
VAR_IN_OUT
  /// Robot assignment of function
  AxesGroup         : AxesGroup; 
END_VAR

/// Push robot: Robot calculates opposite vector and moves slowly in that direction
FUNCTION_BLOCK SoftSwitchTCP EXTENDS RobotLibraryBaseFB
// ------------------------------------------------------------------------- 
//  SRCI Robot Library                                                
// ------------------------------------------------------------------------- 
//                                                                           
//  Object:      SoftSwitchTCP                                       
//  Author:      Thorsten Brach                                                
//  Date:        2024-06-01
//
//  Description:                                              
//   
//                                                                           
//  Copyright: 
//    (C) 2024 Thorsten Brach. All rights reserved                                                                         
//                                                                           
//  Disclaimer:                                                                           
//    This project is provided without any guarantee and can be used for   
//    private and commercial purposes. Any commercial use is at the user’s 
//    own risk and responsibility.
//
// -------------------------------------------------------------------------
VAR_INPUT
  /// Start of the command at the rising edge
  Execute             : BOOL;
  /// Defines type of reference coordinate system at which the robot can be moved in a compliant manner.
  ReferenceType       : ReferenceType;
  /// Index of tool or frame according to ReferenceType.
  /// • 0 (default): Flange/WS
  /// • 1..254: Tool/User frames
  ReferenceNo         : USINT;
  /// Parameter which determines the behavior towards the previously sent and still active or buffered commands
  AbortingMode        : AbortingMode;
  /// Relates to "LimitMode" with the setting "No limit defined". Defines for which cartesian axes the compliant motion is allowed. 
  /// If TRUE, the direction is enabled. Default value is FALSE. See Table 6-744 for bit assignment.
  CompliantAxes       : BYTE;
  /// Defines which parameters will limit the space, where the robot complies (at least one option must be supported)
  LimitMode           : LimitMode;
  /// Defines the type of the resistance force applied at the RA against the external force
  ResistanceForceMode : ResistanceForceMode;
  /// Relates to "ResistanceForceMode" with the setting "ResistanceForceTCP".
  /// Defines the resistance force level applied at the RA against the external force.
  /// • 0..99: Resistance is lower than robot vendor default. The lower the value, the easier the robot can be pushed away.
  /// • 100 (default): The value is aligned with the robot vendor-specific default value.
  /// • 101..200: Resistance is higher than robot vendor default. At maximum value "200" highest possible resistance against external force
  ResistanceForceTCP  : REAL;
  /// Relates to "ResistanceForceMode" with the setting "ResistanceForceAxis".
  /// Defines the resistance force level of the RA at the respective cartesian axis.
  ///  0..99: Resistance is lower than robot vendor default. The lower the value, the easier the robot can be pushed away.
  /// • 100 (default): The value is aligned with the robot vendor-specific default value.
  /// • 101..200: Resistance is higher than robot vendor default. At maximum value "200" highest possible resistance against external force.
  /// Index:
  /// • [0]: X-Axis O
  /// • [1]: Y-axis O
  /// • [2]: Z-axis O
  /// • [3]: RX-Direction O
  /// • [4]: RY-direction O
  /// • [5]: RZ-direction
  ResistanceForceAxis : ARRAY[0..5] OF REAL;
  /// Relates to "LimitMode" with the setting "Limit defined". 
  /// Limit values in the selected cartesian coordinate system (see input parameters ReferenceType and ReferenceNo).
  /// "0" is (default) the origin of the vector, the TCP position, at the start position of the robot.
  /// See Table 6-742 for the parameter index assignment.
  VectorData          : ARRAY[0..5] OF REAL;
  /// Defines the target sequence in which the command will be executed
  SequenceFlag        : SequenceFlag;
END_VAR
VAR_OUTPUT
  /// The command has been completed successfully
  Done                : BOOL;
  /// FB is being processed
  Busy                : BOOL;
  /// Command is transferred and confirmed by the RC
  CommandBuffered     : BOOL;
  /// The command "SoftSwitchTCP" takes control of the according axis group.
  Active              : BOOL;
  /// The command was aborted by another command
  CommandAborted      : BOOL;
  /// TRUE, while command is interrupted during execution and can be continued
  CommandInterrupted  : BOOL;
  /// If TRUE, the RA is in a compliant movement.
  SoftMovement        : BOOL;
END_VAR

METHOD PROTECTED OnCall
VAR_IN_OUT
  /// Robot assignment of function
  AxesGroup         : AxesGroup; 
END_VAR

