/// Read the actual position – TCP: X…RZ + config/Turn, … Joint: (J1…E6)
FUNCTION_BLOCK ReadActualPosition EXTENDS RobotLibraryBaseFB
// ------------------------------------------------------------------------- 
//  SRCI Robot Library                                                
// ------------------------------------------------------------------------- 
//                                                                           
//  Object:      ReadActualPosition                                       
//  Author:      Thorsten Brach                                                
//  Date:        2024-06-01
//
//  Description:                                              
//   
//                                                                           
//  Copyright: 
//    (C) 2024 Thorsten Brach. All rights reserved                                                                         
//                                                                           
//  Disclaimer:                                                                           
//    This project is provided without any guarantee and can be used for   
//    private and commercial purposes. Any commercial use is at the user’s 
//    own risk and responsibility.
//
// -------------------------------------------------------------------------
VAR_INPUT
  /// Start of the command at the rising edge
  Execute            : BOOL;
  /// Processing mode - For more information see chapter 5.6.4.5.
  ProcessingMode     : ProcessingMode;
  /// Index of tool of returned position
  /// •     -1: Currently used tool on RC
  /// •      0: Flange (default)
  /// • 1..254: Tool frames
  ToolNo             : INT;
  /// Index of frame of returned position
  /// •     -1: Currently used frame on RC
  /// •      0: WCS (default)
  /// • 1..254: User frames
  FrameNo            : INT;
  /// Defines the target sequence in which the command will be executed
  SequenceFlag       : SequenceFlag;
  /// ID of associated trigger function
  /// •  0: No Trigger (default) -> No trigger related behavior
  /// • >0: Trigger              -> Start executing when the trigger function with the identical EmitterID is triggered.
  /// Always positive. For more information, see chapter 5.5.12 Triggers
  ListenerID         : SINT;
END_VAR

VAR_OUTPUT
  /// The command has been completed successfully
  Done               : BOOL;
  /// TRUE, while the following outputs return valid values
  /// • ActualCartesianPosition
  /// • ToolNoReturn
  /// • FrameNoReturn
  /// • ActualJointPosition
  Valid              : BOOL;
  /// FB is being processed
  Busy               : BOOL;
  /// Command is transferred and confirmed by the RC
  CommandBuffered    : BOOL;  
  /// The command was aborted by another command.
  CommandAborted     : BOOL;
  /// TRUE, while command is interrupted during execution and can be continued.
  CommandInterrupted : BOOL;
  /// Receiving of input parameter values has been acknowledged by RC
  ParameterAccepted  : BOOL;
  /// Relates to ListenerID >0 Number of successful trigger -based command invocations. For more information refer to chapter 5.5.12.4.
  InvocationCounter  : USINT;
  /// Unique system-generated ID of the "Action" when the function is triggered 
  /// • >0: The "Action" is started by the trigger function with identical FollowID.
  /// • <0: The "Action" is stopped BY the trigger function with identical FollowID.
  /// For more information see chapter 5.5.12.4
  OriginID           : INT;
END_VAR

METHOD PROTECTED OnCall
VAR_IN_OUT
  /// Robot assignment of function
  AxesGroup         : AxesGroup; 
END_VAR

/// Read the actual position cyclically
FUNCTION_BLOCK ReadActualPositionCyclic EXTENDS RobotLibraryBaseFB
// ------------------------------------------------------------------------- 
//  SRCI Robot Library                                                
// ------------------------------------------------------------------------- 
//                                                                           
//  Object:      ReadActualPositionCyclic                                       
//  Author:      Thorsten Brach                                                
//  Date:        2024-06-01
//
//  Description:                                              
//   
//                                                                           
//  Copyright: 
//    (C) 2024 Thorsten Brach. All rights reserved                                                                         
//                                                                           
//  Disclaimer:                                                                           
//    This project is provided without any guarantee and can be used for   
//    private and commercial purposes. Any commercial use is at the user’s 
//    own risk and responsibility.
//
// -------------------------------------------------------------------------
VAR_INPUT
  /// Set TRUE (default) to cyclically transmit position data
  Enable                      : BOOL;
  /// Set TRUE (default) to activate cyclic transmission of cartesian position without external axes
  ReadCartesianPosition       : BOOL;
  /// Set TRUE to activate cyclic transmission of external axes values of cartesian position -  Default: False
  ReadExtCartesianPosition    : BOOL;
  ///Index of tool of returned position
  /// •     -1: Currently used tool on RC
  /// •      0: Flange (default)
  /// • 1..254: Tool frames
  ToolNo                      : INT;
  /// Index of frame of returned position
  /// •     -1: Currently used frame on RC
  /// •      0: WCS (default)
  /// • 1..254: User frames
  FrameNo                     : INT;
  /// Set TRUE (default) to activate cyclic transmission of joint position without external axes
  ReadJointPosition           : BOOL;
  /// Set TRUE to activate cyclic transmission of external axes values of joint position - Default: False
  ReadExtJointPosition        : BOOL;
END_VAR

VAR_OUTPUT
  /// FB is enabled
  Enabled                     : BOOL;
  /// FB is being processed
  Busy                        : BOOL;
  /// TRUE, while the output CartesianPosition returns valid values
  ReadingCartesianPosition    : BOOL;
  /// TRUE, while the output ExtCartesianPosition returns valid values
  ReadingExtCartesianPosition : BOOL;
  /// TRUE, while the output JointPosition returns valid values
  ReadingJointPosition        : BOOL;
  /// TRUE, while the output ExtJointPosition returns valid values
  ReadingExtJointPosition     : BOOL;
  /// Tool and frame index of currently used tool and frame according to Table 6-40
  CurrentCoordinateSystem     : CoordinateSystem;
  /// Cyclically returned, absolute coordinates of current position in selected coordinate systems (see input parameters ToolNo and FrameNo) 
  CartesianPosition           : RobotCartesianPositionShort;
  /// Cyclically returned, absolute cartesian position of the external axes of the robot
  CartesianPositionExt        : RobotCartesianPositionExt;
  /// Tool and frame index of returned position according to Table 6-40
  CoordinateSystem            : CoordinateSystem;
  /// Cyclically returned, absolute position of the robot in Joint position
  JointPosition               : RobotJointPositionShort;
  /// Cyclically returned, absolute joint position of the external axes of the robot
  JointPositionExt            : RobotJointPositionExt;
END_VAR

METHOD PROTECTED OnCall
VAR_IN_OUT
  /// Robot assignment of function
  AxesGroup         : AxesGroup; 
END_VAR

/// Read actual TCP velocity
FUNCTION_BLOCK ReadActualTCPVelocity EXTENDS RobotLibraryBaseFB
// ------------------------------------------------------------------------- 
//  SRCI Robot Library                                                
// ------------------------------------------------------------------------- 
//                                                                           
//  Object:      ReadActualTCPVelocity                                       
//  Author:      Thorsten Brach                                                
//  Date:        2024-06-01
//
//  Description:                                              
//   
//                                                                           
//  Copyright: 
//    (C) 2024 Thorsten Brach. All rights reserved                                                                         
//                                                                           
//  Disclaimer:                                                                           
//    This project is provided without any guarantee and can be used for   
//    private and commercial purposes. Any commercial use is at the user’s 
//    own risk and responsibility.
//
// -------------------------------------------------------------------------
VAR_INPUT
    /// Start of the command at the rising edge
  Execute            : BOOL;
  /// Processing mode
  ProcessingMode     : ProcessingMode;
  /// Index of tool of returned velocity
  /// •      0: Flange (default)
  /// • 1..254: Tool frames
  ToolNo             : USINT;
  /// Index of frame
  /// •      0: WCS (default)
  /// • 1..254: User frames
  FrameNo            : USINT;
  /// Defines the target sequence in which the command will be executed 
  ///  For more information about the differentiation of sequences see chapter 5.5.3.4
  SequenceFlag       : SequenceFlag;
  /// ID of associated trigger function
  /// • 0: No Trigger (default) -> No trigger related behavior
  /// • >0: Trigger             -> Start executing when the trigger function with the identical EmitterID is triggered. 
  /// Always positive.
  /// For more information, see chapter 5.5.12 Triggers
  ListenerID         : SINT;
END_VAR

VAR_OUTPUT
  /// The command has been completed successfully
  Done               : BOOL;
  /// TRUE, while the following outputs return valid values:
  /// • ActualTCPVelocity
  /// • ToolNoReturn
  /// • FrameNoReturn
  Valid              : BOOL;
  /// FB is being processed
  Busy               : BOOL;
  /// The command was aborted by another command.
  CommandAborted     : BOOL;
  /// Command is transferred and confirmed by the RC
  CommandBuffered    : BOOL;
  /// TRUE, while command is interrupted during execution and can be continued
  CommandInterrupted : BOOL;
  /// Receiving of input parameter values has been acknowledged by RC
  ParameterAccepted  : BOOL;
  /// Absolute actual velocity of the robot’s TCP. [mm/s]
  ActualTCPVelocity  : REAL;
  /// Index of tool of returned velocity
  /// •      0: Flange
  /// • 1..254: Tool frames
  ToolNoReturn       : USINT;
  /// Index of frame of returned position
  /// •      0: WCS
  /// • 1..254: User frames
  FrameNoReturn      : USINT;
  /// Unique system-generated ID of the "Action" when the function is triggered
  /// • >0: The "Action" is started by the trigger function with identical FollowID.
  /// • <0: The "Action" is stopped by the trigger function with identical FollowID.
  /// For more information see chapter 5.5.12.4
  OriginID           : INT;
  /// Relates to ListenerID >0 Number of successful trigger -based command invocations 
  /// For more information refer to chapter 5.5.12.4
  InvocationCounter  : USINT;
END_VAR

METHOD PROTECTED OnCall
VAR_IN_OUT
  /// Robot assignment of function
  AxesGroup         : AxesGroup; 
END_VAR

