  /// Defines message codes through which the associated Action will be triggered (relates to Mode 6 and 7)
  MessageCodes           : ARRAY [0..15] OF DWORD;
  /// Defines if parametrization messages are included (relates to Mode 0 to 5)
  /// • FALSE (default): Do not include incorrect parametrization
  /// • TRUE: Include incorrect parametrization
  IncludeParameterValidation : BOOL;
  /// ID of associated trigger function:
  /// • 0: Immediately (default) - Start executing this function immediately.
  /// • >0: Trigger -  Start executing, when the trigger function with the identical EmitterID is triggered.
  /// Always positive.
  /// For more information, see chapter 5.5.12 Triggers
  ListenerID             : SINT;
  /// ID of the Action function that will be executed when the trigger condition is met
  /// • >0: Start Action        - Start executing the Action function with the identical ListenerID.
  /// • <0: Stop Action         - Stop executing the Action function with the identical ListenerID.
  /// •  0: Undefined (default) - If no EmitterID is defined, the function returns an error message
  /// For more information see chapter 5.5.12.4
  EmitterID              : SINT;  
END_VAR
VAR_OUTPUT
  /// The command has been completed successfully
  Done                   : BOOL;
  /// FB is being processed
  Busy                   : BOOL;
  /// Command is transferred and confirmed by the RC
  CommandBuffered        : BOOL;
  /// TRUE, when the monitoring of the input parameter values is active.
  MonitoringActive       : BOOL;
  /// Unique system-generated ID of the "Action" when the function is triggered.
  /// • >0: The "Action" is started by the trigger function with identical FollowID.
  /// • <0: The "Action" is stopped by the trigger function with identical FollowID.
  /// For more information see chapter 5.5.12.4 EmitterID, ListenerID, FollowID and OriginID
  OriginID               : INT;
  /// Relates to ListenerID >0
  /// Number of successful trigger -based command invocations
  /// For more information refer to chapter 5.5.12.4.
  InvocationCounter      : USINT;
  /// Unique system-generated ID of the trigger function when the function is called by user.
  /// For more information see chapter 5.5.12.4.
  FollowID               : DINT;
END_VAR

METHOD PROTECTED OnCall
VAR_IN_OUT
  /// Robot assignment of function
  AxesGroup         : AxesGroup; 
END_VAR

/// Trigger "Actions" based on physical events (e.g. force limit reached)
FUNCTION_BLOCK SetTriggerLimit EXTENDS RobotLibraryBaseFB
// ------------------------------------------------------------------------- 
//  SRCI Robot Library                                                
// ------------------------------------------------------------------------- 
//                                                                           
//  Object:      SetTriggerLimit                                       
//  Author:      Thorsten Brach                                                
//  Date:        2024-06-01
//
//  Description:                                              
//   
//                                                                           
//  Copyright: 
//    (C) 2024 Thorsten Brach. All rights reserved                                                                         
//                                                                           
//  Disclaimer:                                                                           
//    This project is provided without any guarantee and can be used for   
//    private and commercial purposes. Any commercial use is at the user’s 
//    own risk and responsibility.
//
// -------------------------------------------------------------------------
VAR_INPUT
  /// Start of the command at the rising edge
  Execute                : BOOL;
  /// Processing mode
  ProcessingMode         : ProcessingMode;
  /// Specifies the trigger event by exceeding of the following limit values (one of the following options must be supported)
  TriggerMode            : TriggerModeLimit;
  /// Determines whether the start condition is evaluated or not.
  /// • FALSE: Start condition is not evaluated (default).
  /// • TRUE: Start condition is evaluated
  EvaluateStartCondition : BOOL;
  /// If the tolerance limits for the respective actual data values from the RC are exceeded, the Action function that will be executed.
  /// For "TriggerMode" 1, 2 and 4 the tolerance limits must be entered as follows:
  /// • [0..5]: relates to axes 1 to 6
  /// • [6..11]: relates to external axes 1 to 6.
  /// For "TriggerMode" 3 the tolerance limits must be entered as follows:
  /// • [0]: Following Error in mm.
  /// • [1..11]: Undefined and not relevant.
  /// For all array entries default: 999999
  Data_1                 : ARRAY[0..11] OF REAL;
  /// If the respective actual data values from the RC are under the defined limit, the Action function will be stopped.
  /// For "TriggerMode" 1, 2 and 4 the tolerance limits must be entered as follows:
  /// • [0..5]: relates to axes 1 to 6.
  /// • [6..11]: relates to external axes 1 to 6.
  /// For "TriggerMode" 3 the tolerance limits must be entered as follows:
  /// • [0]: Following Error in mm.
  /// • [1..11]: Undefined and not relevant.
  /// For all array entries default: 999999
  Data_2                 : ARRAY[0..11] OF REAL;
  /// ID of associated trigger function:
  /// • 0: Immediately (default) - Start executing this function immediately.
  /// • >0: Trigger -  Start executing, when the trigger function with the identical EmitterID is triggered.
  /// Always positive.
  /// For more information, see chapter 5.5.12 Triggers
  ListenerID             : SINT;
  /// ID of the Action function that will be executed when the trigger condition is met
  /// • >0: Start Action        - Start executing the Action function with the identical ListenerID.
  /// • <0: Stop Action         - Stop executing the Action function with the identical ListenerID.
  /// •  0: Undefined (default) - If no EmitterID is defined, the function returns an error message
  /// For more information see chapter 5.5.12.4
  EmitterID              : SINT;  
END_VAR
VAR_OUTPUT
  /// The command has been completed successfully
  Done                   : BOOL;
  /// TRUE, while the following outputs return valid values:
  /// • Data
  Valid                  : BOOL;
  /// FB is being processed
  Busy                   : BOOL;
  /// Command is transferred and confirmed by the RC
  CommandBuffered        : BOOL;
  /// The respective actual data values from the RC that depend on "TriggerMode".
  Data                   : ARRAY [0..11] OF REAL;  
  /// TRUE, when the monitoring of the input parameter values is active.
  MonitoringActive       : BOOL;
  /// Unique system-generated ID of the "Action" when the function is triggered.
  /// • >0: The "Action" is started by the trigger function with identical FollowID.
  /// • <0: The "Action" is stopped by the trigger function with identical FollowID.
  /// For more information see chapter 5.5.12.4 EmitterID, ListenerID, FollowID and OriginID
  OriginID               : INT;
  /// Relates to ListenerID >0
  /// Number of successful trigger -based command invocations
  /// For more information refer to chapter 5.5.12.4.
  InvocationCounter      : USINT;
  /// Unique system-generated ID of the trigger function when the function is called by user.
  /// For more information see chapter 5.5.12.4.
  FollowID               : DINT;
END_VAR

METHOD PROTECTED OnCall
VAR_IN_OUT
  /// Robot assignment of function
  AxesGroup         : AxesGroup; 
END_VAR

/// Trigger an action based on a motion related parameter (e.g. progress of trajectory)
