//    (C) 2024 Thorsten Brach. All rights reserved                                                                         
//                                                                           
//  Disclaimer:                                                                           
//    This project is provided without any guarantee and can be used for   
//    private and commercial purposes. Any commercial use is at the user’s 
//    own risk and responsibility.
//
// -------------------------------------------------------------------------
VAR_INPUT
  /// Start of the command at the rising edge
  Execute                : BOOL;
  /// Processing mode
  ProcessingMode         : ProcessingMode;
  /// Defines the target sequence in which the command will be executed
  SequenceFlag           : SequenceFlag;
  /// ID of the Action function that will be executed when the trigger condition is met
  /// • >0: Start Action        - Start executing the Action function with the identical ListenerID.
  /// • <0: Stop Action         - Stop executing the Action function with the identical ListenerID.
  /// •  0: Undefined (default) - If no EmitterID is defined, the function returns an error message
  /// For more information see chapter 5.5.12.4
  EmitterID              : SINT;  
END_VAR
VAR_OUTPUT
  /// The command has been completed successfully
  Done                   : BOOL;
  /// FB is being processed
  Busy                   : BOOL;
  /// Command is transferred and confirmed by the RC
  CommandBuffered        : BOOL;
  /// The command was aborted by another command
  CommandAborted         : BOOL;
  /// TRUE, while command is interrupted during execution and can be continued.
  CommandInterrupted     : BOOL;
  /// Unique system-generated ID of the trigger function when the function is called by user.
  /// For more information see chapter 5.5.12.4.
  FollowID               : DINT;
END_VAR

METHOD PROTECTED OnCall
VAR_IN_OUT
  /// Robot assignment of function
  AxesGroup         : AxesGroup; 
END_VAR

/// Wait to process next command in sequence until trigger signal is received
FUNCTION_BLOCK WaitForTrigger EXTENDS RobotLibraryBaseFB
// ------------------------------------------------------------------------- 
//  SRCI Robot Library                                                
// ------------------------------------------------------------------------- 
//                                                                           
//  Object:      WaitForTrigger                                       
//  Author:      Thorsten Brach                                                
//  Date:        2024-06-01
//
//  Description:                                              
//   
//                                                                           
//  Copyright: 
//    (C) 2024 Thorsten Brach. All rights reserved                                                                         
//                                                                           
//  Disclaimer:                                                                           
//    This project is provided without any guarantee and can be used for   
//    private and commercial purposes. Any commercial use is at the user’s 
//    own risk and responsibility.
//
// -------------------------------------------------------------------------
VAR_INPUT
  /// Start of the command at the rising edge
  Execute                : BOOL;
  /// Processing mode
  ProcessingMode         : ProcessingMode;
  ///  Defines the time at which the trigger can be reacted to
  /// • 0: Conditional - Trigger signal accepted as soon as the function is BUFFERED
  /// • 1: Absolute - Trigger signal accepted as soon as the function is ACTIVE
  ConditionalWait        : BOOL;
  /// Defines the target sequence in which the command will be executed
  SequenceFlag           : SequenceFlag;
  /// ID of associated trigger function:
  /// • 0: Immediately (default) - Start executing this function immediately.
  /// • >0: Trigger -  Start executing, when the trigger function with the identical EmitterID is triggered.
  /// Always positive.
  /// For more information, see chapter 5.5.12 Triggers
  ListenerID             : SINT;
END_VAR
VAR_OUTPUT
  /// The command has been completed successfully
  Done                   : BOOL;
  /// FB is being processed
  Busy                   : BOOL;
  /// Command is transferred and confirmed by the RC
  CommandBuffered        : BOOL;
  /// Command is currently processed by the active sequence, takes control of the motion of the according axis group and is waiting for Trigger.
  Active                 : BOOL;
  /// The command was aborted by another command
  CommandAborted         : BOOL;
  /// TRUE, while command is interrupted during execution and can be continued.
  CommandInterrupted     : BOOL;
  /// Unique system-generated ID of the "Action" when the function is triggered.
  /// • >0: The "Action" is started by the trigger function with identical FollowID.
  /// • <0: The "Action" is stopped by the trigger function with identical FollowID.
  /// For more information see chapter 5.5.12.4 EmitterID, ListenerID, FollowID and OriginID
  OriginID               : INT;
  /// Relates to ListenerID >0
  /// Number of successful trigger -based command invocations
  /// For more information refer to chapter 5.5.12.4.
  InvocationCounter      : USINT;
END_VAR

METHOD PROTECTED OnCall
VAR_IN_OUT
  /// Robot assignment of function
  AxesGroup         : AxesGroup; 
END_VAR

/// Write analog output of RC
FUNCTION_BLOCK WriteAnalogOutput EXTENDS RobotLibraryBaseFB
// ------------------------------------------------------------------------- 
//  SRCI Robot Library                                                
// ------------------------------------------------------------------------- 
//                                                                           
//  Object:      WriteAnalogOutput                                       
//  Author:      Thorsten Brach                                                
//  Date:        2024-06-01
//
//  Description:                                              
//   
//                                                                           
//  Copyright: 
//    (C) 2024 Thorsten Brach. All rights reserved                                                                         
//                                                                           
//  Disclaimer:                                                                           
//    This project is provided without any guarantee and can be used for   
//    private and commercial purposes. Any commercial use is at the user’s 
//    own risk and responsibility.
//
// -------------------------------------------------------------------------
VAR_INPUT
  /// Start of the command at the rising edge
  Execute            : BOOL;
  /// Processing Mode
  ProcessingMode     : ProcessingMode;
  /// Number of the analog input 1..32
  Index              : USINT;
  /// Value of analog output
  Value              : REAL;
  /// Unit of set Value:
  Unit               : UnitType;
  /// Set TRUE to prioritize the execution of this command in the defined target sequence.             
  HighPriority       : BOOL;
  /// Defines the target sequence in which the command will be executed
  SequenceFlag       : SequenceFlag;
  /// ID of associated trigger function:
  /// •  0: No Trigger (default)  No trigger related behavior
  /// • >0: Trigger - Start executing when the trigger function with the identical EmitterID is triggered.
  /// Always positive.
  /// For more information, see chapter 5.5.12 Triggers
  ListenerID         : SINT;
END_VAR
VAR_OUTPUT
  /// The command has been completed successfully
  Done               : BOOL;
  /// FB is being processed
  Busy               : BOOL;
  /// Command is transferred and confirmed by the RC
  CommandBuffered    : BOOL;
  /// The command was aborted by another command.
  CommandAborted     : BOOL;
  /// TRUE, while command is interrupted during execution and can be continued
  CommandInterrupted : BOOL;
  /// Receiving of input parameter values has been acknowledged by RC
  ParameterAccepted  : BOOL;
  /// Unique system-generated ID of the "Action" when the function is triggered.
  /// • >0: The "Action" is started by the trigger function with identical FollowID.
  /// • <0: The "Action" is stopped by the trigger function with identical FollowID.
  /// For more information see chapter 5.5.12.4.
  OriginID           : USINT;
  /// Relates to ListenerID >0 
  /// Number of successful trigger -based command invocations
  /// For more information refer to chapter 5.5.12.4.
  InvocationCounter  : USINT;
END_VAR

METHOD PROTECTED OnCall
VAR_IN_OUT
  /// Robot assignment of function
  AxesGroup         : AxesGroup; 
END_VAR

