ST.xhtml:
FOR _idx := 1 TO RobotLibraryParameter.ACTIVE_CMD_REGISTER_ENTRIES_MAX
DO
  IF ( Register[_idx].State = ActiveCommandRegisterState.IS_FREE )
  THEN
    Register[_idx].State                        := ActiveCommandRegisterState.IS_PROCESSING;
    Register[_idx].UniqueID                     := CreateUniqueID();
    Register[_idx].rCommandFB                   := rCommandFB;

    // check reference to command FB
    IF (__ISVALIDREF(Register[_idx].rCommandFB))
    THEN
      Register[_idx].Command[1].Payload    := rCommandFB.CommandData.Payload;
      Register[_idx].Command[1].PayloadLen := rCommandFB.CommandData.PayloadLen;
      Register[_idx].Command[1].PayLoadPtr := 0;
      Register[_idx].Command[1].State      := BufferStateCmd.CREATED;
		END_IF

    // return Unique ID
    AddCmd := Register[_idx].UniqueID;
    EXIT;
	END_IF
END_FOR

InterfaceAsPlainText.xhtml:
METHOD INTERNAL AddCmd : UDINT
VAR_INPUT
  /// Reference to the corresponding command-FB to enable a callback mechanism
  rCommandFB   : REFERENCE TO RobotLibraryBaseFB;
END_VAR

VAR
  /// internal index for loops
 _idx : DINT;
END_VAR

ST.xhtml:
// inc last unique ID 
LastUniqueID := LastUniqueID + 1;
// return unique ID
CreateUniqueID := LastUniqueID;

OnlineChange := -1;

// Check command FB reference is valid ?
IF ( NOT __ISVALIDREF(rCommandFB))
THEN
  RETURN;
END_IF

FOR _idx := 1 TO RobotLibraryParameter.ACTIVE_CMD_REGISTER_ENTRIES_MAX
DO
  // check unique ID found ?
  IF ( Register[_idx].UniqueID = UniqueID ) 
  THEN  
    // update reference to command FB
    Register[_idx].rCommandFB := rCommandFB;
    // Return result OK
    OnlineChange := RobotLibraryConstants.OK;
    EXIT;
  END_IF
END_FOR

InterfaceAsPlainText.xhtml:
METHOD OnlineChange : DINT
VAR_INPUT
  /// unique ID 
  UniqueID     : UDINT;
  /// Reference to the corresponding command-FB to enable a callback mechanism
  rCommandFB   : REFERENCE TO RobotLibraryBaseFB;
END_VAR

VAR
  /// internal index for loops
 _idx : DINT;
END_VAR

ST.xhtml:
FOR _idx := 1 TO RobotLibraryParameter.ACTIVE_CMD_REGISTER_ENTRIES_MAX
DO
  // check unique ID found and not yet sended ?
  IF (( Register[_idx].UniqueID         = UniqueID               ) AND
      ( Register[_idx].Command[1].State < BufferStateCmd.SENDING ))       
  THEN  
    // delete register entry
    MemSet(ADR(Register[_idx]), 0 , SIZEOF(Register[_idx]));
    EXIT;
  END_IF
END_FOR

