  /// internal index
 _idx : DINT;
END_VAR

ST.xhtml:
// Bubble Sort 
FOR i := 1 TO RobotLibraryParameter.ACTIVE_CMD_REGISTER_ENTRIES_MAX
DO
  FOR j := 1 TO RobotLibraryParameter.ACTIVE_CMD_REGISTER_ENTRIES_MAX - 1
  DO
    IF ( List[j] > List[j+1])
    THEN
       Tmp := List[j];         
       List[j]   := List[j+1];
       List[j+1] := Tmp;
    END_IF
  END_FOR 
END_FOR

// return sorted list
SortList := List;

InterfaceAsPlainText.xhtml:
METHOD SortList : ARRAY [1..RobotLibraryParameter.ACTIVE_CMD_REGISTER_ENTRIES_MAX] OF UINT;
VAR_INPUT
  List : ARRAY [1..RobotLibraryParameter.ACTIVE_CMD_REGISTER_ENTRIES_MAX] OF UINT;
END_VAR

VAR
  i   : DINT;
  j   : DINT;
  tmp : UINT;
END_VAR

